      },
      validation: {
        valid: validation.valid,
        errors: validation.errors
      },
      timestamp: new Date().toISOString()
    });

  } catch (error) {
    console.error('Failed to get Tier 1 status:', error);
    return res.status(500).json({
      success: false,
      error: 'Failed to retrieve status'
