/**
 * Maps AI error codes to HTTP status codes
 */
function getErrorStatusCode(code: AIAnalysisError['code']): number {
  switch (code) {
    case 'DATABRICKS_AUTH':
      return 401;
    case 'DATABRICKS_QUOTA':
      return 429;
    case 'DATABRICKS_TIMEOUT':
      return 504;
    case 'INVALID_INPUT':
      return 400;
    case 'ANALYSIS_FAILED':
    default:
      return 500;
  }
}

