/**
 * Get admin security statistics for monitoring
 */
export async function getAdminSecurityStats(timeframeHours: number = 24): Promise<{
  totalAccesses: number;
  deniedAccesses: number;
  topRoutes: Array<{ route: string; count: number }>;
  securityViolations: number;
  roleBreakdown: Record<string, number>;
}> {
  try {
    const timeframeStart = new Date(Date.now() - (timeframeHours * 60 * 60 * 1000));

    const stats = await databricksService.query(`
      SELECT 
        COUNT(*) as total_accesses,
        SUM(CASE WHEN description LIKE '%ADMIN_ACCESS_DENIED%' THEN 1 ELSE 0 END) as denied_accesses,
        SUM(CASE WHEN description LIKE '%ROUTE_SECURITY_VIOLATION%' THEN 1 ELSE 0 END) as security_violations,
        resource_id as route_path,
        COUNT(*) as access_count
      FROM ${databricksConfig.catalog}.compliance.audit_log
      WHERE event_type = 'admin_route_access' 
        AND event_category = 'compliance'
        AND event_timestamp >= ?
      GROUP BY resource_id
      ORDER BY access_count DESC
      LIMIT 50
    `, [timeframeStart.toISOString()]);

    // Process results (simplified aggregation)
    const result = {
      totalAccesses: 0,
      deniedAccesses: 0,
      topRoutes: [] as Array<{ route: string; count: number }>,
      securityViolations: 0,
      roleBreakdown: {} as Record<string, number>
    };

    // Aggregate basic stats from rows
    for (const row of stats as any[]) {
      result.totalAccesses += Number(row.total_accesses || 0);
      result.deniedAccesses += Number(row.denied_accesses || 0);
      result.securityViolations += Number(row.security_violations || 0);
      result.topRoutes.push({ route: row.route_path, count: Number(row.access_count || 0) });
    }

    // Note: role breakdown unavailable in canonical schema; return empty breakdown
    return result;

  } catch (error) {
    // Gracefully handle missing audit log table
    const errorMessage = error instanceof Error ? error.message : String(error);
    if (errorMessage.includes('TABLE_OR_VIEW_NOT_FOUND')) {
      console.warn('Audit log table not found - returning empty admin security stats (non-critical)');
    } else {
      console.error('Error fetching admin security stats from audit log:', error);
    }

    return {
      totalAccesses: 0,
      deniedAccesses: 0,
      topRoutes: [],
      securityViolations: 0,
      roleBreakdown: {}
    };
  }
}

import { Request, Response, NextFunction } from 'express';
import { verifyToken, JWTPayload } from '../utils/jwt.utils';
import { AuthRequest } from '../types/auth.types';
import { redisService } from '../services/redis.service';
import { SecureJWTService } from '../services/secure-jwt.service';
import { SecureSessionService } from '../services/secure-session.service';

export async function authenticate(req: Request, res: Response, next: NextFunction) {
  const authStart = performance.now();
  console.log('üîê AUTH MIDDLEWARE START');
  console.log('üìã Request headers:', JSON.stringify({
    authorization: req.headers.authorization ? 'Bearer ***' : 'none',
    cookie: req.headers.cookie || 'none'
  }, null, 2));
  console.log('üç™ Parsed cookies:', req.cookies);

  try {
    const authHeader = req.headers.authorization;
    let token: string | null = null;
    let sessionId: string | null = null;

    // Try to get token from Authorization header first
    if (authHeader && authHeader.startsWith('Bearer ')) {
      token = authHeader.substring(7); // Remove 'Bearer ' prefix
    } 
    // Fallback: Check for session cookie (for session restoration)
    else if (req.cookies?.session_id) {
      sessionId = req.cookies.session_id;
      console.log('üç™ Using session cookie for authentication');
    } 
    else {
      return res.status(401).json({
        error: 'UNAUTHORIZED',
        message: 'No valid authorization token provided',
      });
    }

    try {
      let effectiveSessionId: string;

      if (token) {
        // Basic sanity check: JWT should contain two dots
        const looksLikeJwt = token.split('.').length === 3;
        try {
          if (!looksLikeJwt) {
            throw new Error('Token does not look like a JWT');
          }

          let payload: JWTPayload;

          // In test mode with E2E_TEST_SECRET, use basic JWT verification
          if (process.env.NODE_ENV === 'test' && process.env.E2E_TEST_SECRET) {
            console.log('üß™ Using test mode JWT verification');
            payload = verifyToken(token) as JWTPayload;
          } else {
            // Verify JWT token with enhanced security
            const securePayload = await SecureJWTService.verifyTokenSecurity(token, req, 'access');
            if (!securePayload) {
              return res.status(401).json({
                error: 'INVALID_TOKEN',
                message: 'Invalid or expired token',
              });
            }
            // Convert SecureJWTPayload to JWTPayload
            payload = {
              userId: securePayload.userId,
              email: securePayload.email,
              schoolId: securePayload.schoolId,
              role: securePayload.role,
              sessionId: securePayload.sessionId,
              type: securePayload.type
            };
          }
          if (payload.type !== 'access') {
            return res.status(401).json({
              error: 'INVALID_TOKEN_TYPE',
              message: 'Invalid token type',
            });
          }
          // PREFER session cookie if present, since it represents the current active session
          // The JWT sessionId might be from a previous session that got rotated
          effectiveSessionId = req.cookies?.session_id || payload.sessionId;

          // Test-mode fallback: populate req.user directly from token when Redis is not part of unit tests
          if (process.env.NODE_ENV === 'test') {
            (req as AuthRequest).user = {
              id: payload.userId,
              email: payload.email,
              school_id: payload.schoolId,
              role: payload.role,
              status: 'active',
              access_level: payload.role === 'admin' ? 'admin' : 'teacher',
              max_concurrent_sessions: 5,
              current_sessions: 1,
              timezone: 'UTC',
            } as any;
            (req as AuthRequest).school = { id: payload.schoolId } as any;
            (req as AuthRequest).sessionId = effectiveSessionId;
            return next();
          }
        } catch (e) {
          // Fallback to cookie-based session if available
          if (req.cookies?.session_id) {
            console.warn('‚ö†Ô∏è  Authorization token invalid; falling back to session cookie');
            effectiveSessionId = req.cookies.session_id;
          } else {
            console.error('Token verification error:', e);
            return res.status(401).json({
              error: 'INVALID_TOKEN',
              message: 'Invalid or expired token',
            });
          }
        }
      } else {
        // Use session ID from cookie directly
        effectiveSessionId = sessionId!;
      }

      // Check if session exists using secure session service
      const sessionLookupStart = performance.now();
      const sessionData = await SecureSessionService.getSecureSession(effectiveSessionId, req);
      console.log(`‚è±Ô∏è  Secure session lookup took ${(performance.now() - sessionLookupStart).toFixed(2)}ms`);

      if (!sessionData) {
        // Enhanced logging for rotation debugging
        console.warn(`üö® Session lookup failed for session ID: ${effectiveSessionId}`);
        console.warn('üö® This could be due to:');
        console.warn('   1. Session expired naturally');
        console.warn('   2. Token rotation in progress (deviceFingerprint mismatch)');
        console.warn('   3. Session invalidated by security policy');
        console.warn(`   4. JWT token source: ${token ? 'Bearer token' : 'session cookie'}`);

        // For token rotation scenarios, provide a more specific error
        // This helps clients understand they may need to re-authenticate or retry
        const errorResponse = {
          error: 'SESSION_EXPIRED',
          message: 'Session has expired or been invalidated',
          // Add rotation hint for debugging (development only)
          ...(process.env.NODE_ENV === 'development' && {
            debug: {
              sessionId: effectiveSessionId,
              tokenSource: token ? 'jwt' : 'cookie',
              hint: 'If this occurs during token rotation, the session may be updating'
            }
          })
        };

        return res.status(401).json(errorResponse);
      }

      // Add user info to request from Redis session
      (req as AuthRequest).user = sessionData.teacher;
      (req as AuthRequest).school = sessionData.school;
      (req as AuthRequest).sessionId = effectiveSessionId;

      const authTotal = performance.now() - authStart;
      console.log(`üîê AUTH MIDDLEWARE COMPLETE - Total time: ${authTotal.toFixed(2)}ms`);

      next();
    } catch (tokenError) {
      console.error('Authentication error:', tokenError);
      return res.status(500).json({
        error: 'AUTHENTICATION_ERROR',
        message: 'An error occurred during authentication',
      });
    }
  } catch (error) {
    console.error('Authentication error:', error);
    return res.status(500).json({
      error: 'AUTHENTICATION_ERROR',
      message: 'An error occurred during authentication',
    });
  }
}

export function requireRole(roles: string[]) {
  return (req: Request, res: Response, next: NextFunction) => {
    const authReq = req as AuthRequest;

    if (!authReq.user) {
      return res.status(401).json({
        error: 'UNAUTHORIZED',
        message: 'Authentication required',
      });
    }

    if (!roles.includes(authReq.user.role)) {
      return res.status(403).json({
        error: 'FORBIDDEN',
        message: 'Insufficient permissions',
        required: roles,
        current: authReq.user.role,
      });
    }

    next();
  };
}

export function optionalAuth(req: Request, res: Response, next: NextFunction) {
  const authHeader = req.headers.authorization;

  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    // No token provided, continue without authentication
    return next();
  }

  // If token is provided, validate it
  authenticate(req, res, next);
}

import { Request, Response, NextFunction } from 'express';
import { cacheManager, CacheTTLConfig } from '../services/cache-manager.service';
import { cacheEventBus } from '../services/cache-event-bus.service';
import { AuthRequest } from '../types/auth.types';

/**
 * Express Middleware for Automatic Cache Management
 * Provides declarative caching through route-level configuration
 */

export interface CacheConfig {
  key: string | ((req: Request) => string);
  tags: string[] | ((req: Request) => string[]);
  ttl?: number | string; // number (seconds) or TTL config key
  condition?: (req: Request) => boolean;
  autoInvalidate?: boolean;
  namespace?: string;
}

export interface CacheMiddlewareOptions {
  enabled?: boolean;
  defaultTTL?: number;
  skipOnError?: boolean;
  logLevel?: 'debug' | 'info' | 'warn' | 'error' | 'none';
}

/**
 * Cache response middleware factory
 */
export function cacheResponse(config: CacheConfig, options: CacheMiddlewareOptions = {}) {
  const {
    enabled = true,
    defaultTTL = 300,
    skipOnError = true,
    logLevel = 'info'
  } = options;

  return async (req: Request, res: Response, next: NextFunction) => {
    if (!enabled) {
      return next();
    }

    try {
      // Check cache condition
      if (config.condition && !config.condition(req)) {
        return next();
      }

      // Generate cache key
      const cacheKey = typeof config.key === 'function' 
        ? config.key(req) 
        : config.key;

      // Generate cache tags
      const tags = typeof config.tags === 'function'
        ? config.tags(req)
        : config.tags;

      // Determine TTL
      const ttl = typeof config.ttl === 'string'
        ? CacheTTLConfig[config.ttl as keyof typeof CacheTTLConfig] || defaultTTL
        : config.ttl || defaultTTL;

      // Try to get from cache
      const cached = await cacheManager.get(cacheKey);

      if (cached) {
        if (logLevel !== 'none') {
          console.log(`‚ö° Cache HIT: ${cacheKey}`);
        }

        // Return cached response
        return res.json(cached);
      }

      // Cache miss - intercept response
      const originalJson = res.json;

      res.json = function(data: any) {
        // Store in cache asynchronously
        cacheManager.set(cacheKey, data, {
          tags,
          ttl,
          namespace: config.namespace,
          autoWarm: false
        }).catch(error => {
          if (logLevel !== 'none') {
            console.warn(`‚ö†Ô∏è Cache set failed for ${cacheKey}:`, error);
          }
        });

        if (logLevel !== 'none') {
          console.log(`üíæ Cache SET: ${cacheKey} (TTL: ${ttl}s, Tags: [${tags.join(', ')}])`);
        }

        // Call original json method
        return originalJson.call(this, data);
      };

      next();

    } catch (error) {
      if (skipOnError) {
        console.warn('Cache middleware error (skipping):', error);
        next();
      } else {
        next(error);
      }
    }
  };
}

/**
 * Cache invalidation middleware for mutation operations
 */
export function invalidateCache(tags: string[] | ((req: Request) => string[])) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      // Store original methods
      const originalJson = res.json;
      const originalSend = res.send;

      // Invalidate after successful response
      const handleSuccess = async () => {
        try {
          const tagsToInvalidate = typeof tags === 'function' ? tags(req) : tags;

          await Promise.all(
            tagsToInvalidate.map(tag => cacheManager.invalidateByTag(tag))
          );

          console.log(`üóëÔ∏è Cache invalidated tags: [${tagsToInvalidate.join(', ')}]`);
        } catch (error) {
          console.warn('Cache invalidation error:', error);
        }
      };

      // Override json method
      res.json = function(data: any) {
        if (res.statusCode >= 200 && res.statusCode < 300) {
          setImmediate(handleSuccess); // Don't block response
        }
        return originalJson.call(this, data);
      };

      // Override send method
      res.send = function(data: any) {
        if (res.statusCode >= 200 && res.statusCode < 300) {
          setImmediate(handleSuccess); // Don't block response
        }
        return originalSend.call(this, data);
      };

      next();

    } catch (error) {
      console.warn('Cache invalidation middleware error:', error);
      next(error);
    }
  };
}

/**
