import { Request, Response, NextFunction } from 'express';
import * as crypto from 'crypto';
import { redisService } from '../services/redis.service';
import { AuthRequest } from '../types/auth.types';

const CSRF_TOKEN_LENGTH = 32;
const CSRF_TOKEN_EXPIRY = 3600; // 1 hour
const CSRF_HEADER_NAME = 'X-CSRF-Token';
const CSRF_COOKIE_NAME = 'csrf-token';

// Safe methods that don't require CSRF protection
const SAFE_METHODS = ['GET', 'HEAD', 'OPTIONS'];

// Generate a secure CSRF token
export function generateCSRFToken(): string {
  return crypto.randomBytes(CSRF_TOKEN_LENGTH).toString('hex');
}

// Store CSRF token in Redis
async function storeCSRFToken(sessionId: string, token: string): Promise<void> {
  const key = `csrf:${sessionId}`;
  await redisService.getClient().setex(key, CSRF_TOKEN_EXPIRY, token);
}

// Validate CSRF token from Redis
async function validateCSRFToken(sessionId: string, token: string): Promise<boolean> {
  const key = `csrf:${sessionId}`;
  const storedToken = await redisService.getClient().get(key);

  if (!storedToken || !token) {
    return false;
  }

  // Use timing-safe comparison to prevent timing attacks
  return crypto.timingSafeEqual(
    Buffer.from(storedToken),
    Buffer.from(token)
  );
}

// Middleware to generate and attach CSRF token
export async function csrfTokenGenerator(req: Request, res: Response, next: NextFunction) {
  const authReq = req as AuthRequest;

  // Skip for unauthenticated requests
  if (!authReq.sessionId) {
    return next();
  }

  // Skip for safe methods
  if (SAFE_METHODS.includes(req.method)) {
    // Generate new token for GET requests to forms
    const token = generateCSRFToken();
    await storeCSRFToken(authReq.sessionId, token);

    // Attach token to response locals for template rendering
    res.locals.csrfToken = token;

    // Set CSRF token cookie (httpOnly: false so JS can read it)
    res.cookie(CSRF_COOKIE_NAME, token, {
      httpOnly: false,
      secure: process.env.NODE_ENV === 'production',
      sameSite: process.env.NODE_ENV === 'production' ? 'lax' : 'lax',
      maxAge: CSRF_TOKEN_EXPIRY * 1000
    });
  }

  next();
}

// Middleware to validate CSRF token
export async function csrfProtection(req: Request, res: Response, next: NextFunction) {
  const authReq = req as AuthRequest;

  // Skip for unauthenticated requests
  if (!authReq.sessionId) {
    return next();
  }

  // Skip for safe methods
  if (SAFE_METHODS.includes(req.method)) {
    return next();
  }

  // Get token from header or body
  const token = req.headers[CSRF_HEADER_NAME.toLowerCase()] as string ||
                req.body?._csrf ||
                req.query?._csrf as string;

  if (!token) {
    return res.status(403).json({
      error: 'CSRF_TOKEN_MISSING',
      message: 'CSRF token is required for this request'
    });
  }

  // Validate token
  const isValid = await validateCSRFToken(authReq.sessionId, token);

  if (!isValid) {
    return res.status(403).json({
      error: 'CSRF_TOKEN_INVALID',
      message: 'Invalid CSRF token'
    });
  }

  // Token is valid, continue
  next();
}

// Helper function to get CSRF token for a session
export async function getCSRFToken(sessionId: string): Promise<string | null> {
  const key = `csrf:${sessionId}`;
  return await redisService.getClient().get(key);
}

// Helper function to invalidate CSRF token
export async function invalidateCSRFToken(sessionId: string): Promise<void> {
  const key = `csrf:${sessionId}`;
  await redisService.getClient().del(key);
}

// Middleware factory for selective CSRF protection
export function requireCSRF(options?: { 
  skipRoutes?: string[],
  customHeader?: string 
}) {
  const skipRoutes = options?.skipRoutes || [];
  const headerName = options?.customHeader || CSRF_HEADER_NAME;

  return async (req: Request, res: Response, next: NextFunction) => {
    // Skip if route is in skip list
    if (skipRoutes.some(route => req.path.startsWith(route))) {
      return next();
    }

    // Apply CSRF protection
    await csrfProtection(req, res, next);
  };
}

import { Request, Response, NextFunction } from 'express';
import { AppError } from '../utils/errors';

export const errorHandler = (
  err: Error | AppError,
  req: Request,
  res: Response,
  next: NextFunction
): void => {
  // Default to 500 server error
  let statusCode = 500;
  let message = 'Internal Server Error';
  let errors: any[] = [];

  // Handle known error types
  if (err instanceof AppError) {
    statusCode = err.statusCode;
    message = err.message;
    errors = err.errors || [];
  } else if (err.name === 'ValidationError') {
    statusCode = 400;
    message = 'Validation Error';
    errors = [err.message];
  } else if (err.name === 'UnauthorizedError') {
    statusCode = 401;
    message = 'Unauthorized';
  } else if (err.name === 'JsonWebTokenError') {
    statusCode = 401;
    message = 'Invalid token';
  } else if (err.name === 'TokenExpiredError') {
    statusCode = 401;
    message = 'Token expired';
  }

  // Log error details
  console.error('Error:', {
    statusCode,
    message,
    stack: err.stack,
    path: req.path,
    method: req.method,
  });

  // Send error response
  res.status(statusCode).json({
    error: {
      message,
      errors,
      ...(process.env.NODE_ENV === 'development' && { stack: err.stack }),
    },
  });
};

export const notFoundHandler = (req: Request, res: Response): void => {
  res.status(404).json({
    error: {
      message: 'Resource not found',
      path: req.path,
    },
  });
};

import { Request, Response } from 'express';
import { RateLimiterRedis, RateLimiterMemory } from 'rate-limiter-flexible';
import { redisService } from '../services/redis.service';

// Fallback to memory store if Redis is not available
let rateLimiter: RateLimiterRedis | RateLimiterMemory;
let authRateLimiter: RateLimiterRedis | RateLimiterMemory;
let rateLimiterInitialized = false;
let authRateLimiterInitialized = false;

// Initialize rate limiter with Redis or memory fallback
async function initializeRateLimiter() {
  try {
    if (redisService.isConnected()) {
      const redisClient = redisService.getClient();

      rateLimiter = new RateLimiterRedis({
        storeClient: redisClient,
        keyPrefix: 'rl:general',
        points: process.env.NODE_ENV === 'development' ? 1000 : 100, // Higher limit for dev
        duration: 900, // Per 15 minutes (in seconds)
        blockDuration: process.env.NODE_ENV === 'development' ? 60 : 900, // Shorter block for dev
        execEvenly: true, // Spread requests evenly
      });

      rateLimiterInitialized = true;
      console.log('‚úÖ Rate limiter initialized with Redis');
    } else {
      throw new Error('Redis not connected');
    }
  } catch (error) {
    console.warn('‚ö†Ô∏è  Rate limiter falling back to memory store:', error);

    rateLimiter = new RateLimiterMemory({
      keyPrefix: 'rl:general',
      points: process.env.NODE_ENV === 'development' ? 1000 : 100, // Higher limit for dev
      duration: 900,
      blockDuration: process.env.NODE_ENV === 'development' ? 60 : 900, // Shorter block for dev
      execEvenly: true,
    });

    rateLimiterInitialized = true;
  }
}

// Auth endpoints rate limiter (stricter)

async function initializeAuthRateLimiter() {
  try {
    if (redisService.isConnected()) {
      const redisClient = redisService.getClient();

      authRateLimiter = new RateLimiterRedis({
        storeClient: redisClient,
        keyPrefix: 'rl:auth',
        points: process.env.NODE_ENV === 'development' ? 50 : 5, // 50 for dev, 5 for prod
        duration: 900, // Per 15 minutes
        blockDuration: process.env.NODE_ENV === 'development' ? 60 : 900, // 1 min dev, 15 min prod
        execEvenly: false,
      });

      authRateLimiterInitialized = true;
      console.log('‚úÖ Auth rate limiter initialized with Redis');
    } else {
      throw new Error('Redis not connected');
    }
  } catch (error) {
    console.warn('‚ö†Ô∏è  Auth rate limiter falling back to memory store:', error);

    authRateLimiter = new RateLimiterMemory({
      keyPrefix: 'rl:auth',
      points: process.env.NODE_ENV === 'development' ? 50 : 5, // 50 for dev, 5 for prod
      duration: 900,
      blockDuration: process.env.NODE_ENV === 'development' ? 60 : 900, // 1 min dev, 15 min prod
      execEvenly: false,
    });

    authRateLimiterInitialized = true;
  }
}

// Initialize both rate limiters
export async function initializeRateLimiters() {
  await initializeRateLimiter();
  await initializeAuthRateLimiter();
}

// General rate limiting middleware
export const rateLimitMiddleware = async (req: Request, res: Response, next: Function) => {
  if (process.env.NODE_ENV === 'test') {
    return next();
  }
  // If rate limiter hasn't been initialized yet, allow the request but log warning
  if (!rateLimiterInitialized) {
    console.warn('‚ö†Ô∏è  Rate limiter not initialized, allowing request');
    return next();
  }

  try {
    const key = req.ip || 'unknown';

    // Add timeout to prevent Redis hanging
    const rateLimitPromise = rateLimiter.consume(key);
    const timeoutPromise = new Promise((_, reject) => 
      setTimeout(() => reject(new Error('Rate limit timeout')), 2000)
    );

    await Promise.race([rateLimitPromise, timeoutPromise]);
    next();
  } catch (rejRes: any) {
    if (rejRes.message === 'Rate limit timeout') {
      console.warn('‚ö†Ô∏è  Rate limiter timeout, allowing request');
      return next();
    }

    res.status(429).json({
      error: 'RATE_LIMIT_EXCEEDED',
      message: 'Too many requests, please try again later',
      retryAfter: Math.round(rejRes.msBeforeNext / 1000) || 900,
    });
  }
};

