import { Request, Response, NextFunction } from 'express';
import { AuthRequest } from '../types/auth.types';
import { databricksService } from '../services/databricks.service';

/**
 * Middleware to verify that the authenticated user has access to the requested session
 * Expects sessionId to be available in req.params.sessionId
 */
export async function requireSessionAccess(req: Request, res: Response, next: NextFunction): Promise<Response | void> {
  const authReq = req as AuthRequest;
  const { sessionId } = req.params;

  // Check if user is authenticated (should be handled by auth middleware first)
  if (!authReq.user) {
    return res.status(401).json({
      success: false,
      error: {
        code: 'UNAUTHORIZED',
        message: 'Authentication required',
      },
    });
  }

  // Check if sessionId is provided
  if (!sessionId) {
    return res.status(400).json({
      success: false,
      error: {
        code: 'SESSION_ID_REQUIRED',
        message: 'Session ID is required in the URL path',
      },
    });
  }

  try {
    // Verify session exists and user has access
    const session = await databricksService.queryOne(`
      SELECT 
        cs.id,
        cs.teacher_id,
        cs.school_id,
        cs.status
      FROM classwaves.sessions.classroom_sessions cs
      WHERE cs.id = ?
    `, [sessionId]);

    if (!session) {
      return res.status(404).json({
        success: false,
        error: {
          code: 'SESSION_NOT_FOUND',
          message: 'Session not found',
        },
      });
    }

    // Check ownership - teacher must own the session
    if (session.teacher_id !== authReq.user.id) {
      // Additional check: admin users can access sessions from their school, super_admin can access any session
      if ((authReq.user.role === 'admin' && session.school_id === authReq.user.school_id) || 
          authReq.user.role === 'super_admin') {
        // Admin/Super admin access granted - continue
        console.log(`üîê ${authReq.user.role} access granted for session ${sessionId} by user ${authReq.user.id}`);
      } else {
        // Record unauthorized access attempt for security monitoring
        console.warn(`‚ö†Ô∏è Unauthorized session access attempt:`, {
          userId: authReq.user.id,
          userEmail: authReq.user.email,
          sessionId,
          sessionOwner: session.teacher_id,
          userSchool: authReq.user.school_id,
          sessionSchool: session.school_id,
          userRole: authReq.user.role,
          ip: req.ip,
          userAgent: req.headers['user-agent']
        });

        return res.status(403).json({
          success: false,
          error: {
            code: 'SESSION_ACCESS_DENIED',
            message: 'You do not have permission to access this session',
          },
        });
      }
    }

    // School-level verification for additional security (super_admin users bypass this check)
    if (authReq.user.role !== 'super_admin' && session.school_id !== authReq.user.school_id) {
      console.error(`üö® Security violation: Cross-school session access attempt:`, {
        userId: authReq.user.id,
        userSchool: authReq.user.school_id,
        sessionSchool: session.school_id,
        sessionId
      });

      return res.status(403).json({
        success: false,
        error: {
          code: 'CROSS_SCHOOL_ACCESS_DENIED',
          message: 'Cross-school session access is not permitted',
        },
      });
    }

    // Optional: Check if session is in a valid state for analytics access
    if (req.path.includes('/analytics/') && session.status === 'created') {
      return res.status(400).json({
        success: false,
        error: {
          code: 'SESSION_NOT_READY',
          message: 'Analytics not available for sessions that have not started',
        },
      });
    }

    // Attach session info to request for downstream use
    (authReq as any).sessionInfo = {
      id: session.id,
      teacherId: session.teacher_id,
      schoolId: session.school_id,
      status: session.status
    };

    console.log(`‚úÖ Session access authorized: ${sessionId} for user ${authReq.user.id}`);
    next();

  } catch (error) {
    console.error('Session authorization error:', error);
    return res.status(500).json({
      success: false,
      error: {
        code: 'SESSION_AUTH_ERROR',
        message: 'An error occurred while verifying session access',
      },
    });
  }
}

/**
 * Middleware specifically for analytics endpoints with enhanced logging
 */
export async function requireAnalyticsAccess(req: Request, res: Response, next: NextFunction): Promise<Response | void> {
  const authReq = req as AuthRequest;

  // First check session access
  const sessionCheck = await new Promise<boolean>((resolve) => {
    requireSessionAccess(req, res, (err?: any) => {
      if (err || res.headersSent) {
        resolve(false);
      } else {
        resolve(true);
      }
    });
  });

  if (!sessionCheck) {
    // Response already sent by requireSessionAccess
    return;
  }

  // Additional analytics-specific checks
  const sessionInfo = (authReq as any).sessionInfo;

  // Log analytics access for audit compliance (FERPA/COPPA)
  try {
    await databricksService.recordAuditLog({
      actorId: authReq.user!.id,
      actorType: 'teacher',
      eventType: 'analytics_access',
      eventCategory: 'data_access',
      resourceType: 'session_analytics',
      resourceId: sessionInfo.id,
      schoolId: sessionInfo.schoolId,
      description: `Teacher accessed analytics for session`,
      ipAddress: req.ip,
      userAgent: req.headers['user-agent'],
      complianceBasis: 'legitimate_interest', // Educational improvement
      dataAccessed: req.path.includes('membership-summary') ? 'membership_analytics' : 'session_analytics'
    });
  } catch (auditError) {
    console.error('Failed to log analytics access:', auditError);
    // Continue - don't block access due to audit logging failure
  }

  next();
}

/**
 * Role-based access middleware for super-admin analytics access
 */
export function requireAdminAnalyticsAccess(req: Request, res: Response, next: NextFunction): Response | void {
  const authReq = req as AuthRequest;

  if (!authReq.user) {
    return res.status(401).json({
      success: false,
      error: {
        code: 'UNAUTHORIZED',
        message: 'Authentication required',
      },
    });
  }

  if (authReq.user.role !== 'admin' && authReq.user.role !== 'super_admin') {
    return res.status(403).json({
      success: false,
      error: {
        code: 'ADMIN_ACCESS_REQUIRED',
        message: 'Administrator privileges required for this operation',
      },
    });
  }

  next();
}

import { Request, Response, NextFunction } from 'express';
import { ZodError, ZodSchema } from 'zod';

export function validate(schema: ZodSchema) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      console.log('üîß DEBUG: Validation middleware started');
      console.log('üîß DEBUG: Request body to validate:', req.body);
      console.log('üîß DEBUG: Schema type:', schema.constructor.name);

      req.body = await schema.parseAsync(req.body);

      console.log('üîß DEBUG: Validation successful, parsed body:', req.body);
      next();
    } catch (error) {
      console.error('üîß DEBUG: Validation failed:', error);

      if (error instanceof ZodError) {
        console.error('üîß DEBUG: Zod validation errors:', error.issues);
        return res.status(400).json({
          error: 'VALIDATION_ERROR',
          message: 'Invalid request data',
          details: error.issues.map((err: any) => ({
            field: err.path.join('.'),
            message: typeof err.message === 'string' && err.message.includes('Invalid option')
              ? err.message.replace('Invalid option', 'Invalid enum value')
              : err.message,
          })),
        });
      }

      console.error('üîß DEBUG: Non-Zod validation error:', error);
      return res.status(500).json({
        error: 'INTERNAL_ERROR',
        message: 'An unexpected error occurred',
      });
    }
  };
}

export function validateQuery(schema: ZodSchema) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const validated = await schema.parseAsync(req.query);
      req.query = validated as any;
      next();
    } catch (error) {
      if (error instanceof ZodError) {
        return res.status(400).json({
          error: 'VALIDATION_ERROR',
          message: 'Invalid query parameters',
          details: error.issues.map((err: any) => ({
            field: err.path.join('.'),
            message: typeof err.message === 'string' && err.message.includes('Invalid option')
              ? err.message.replace('Invalid option', 'Invalid enum value')
              : err.message,
          })),
        });
      }

      return res.status(500).json({
        error: 'INTERNAL_ERROR',
        message: 'An unexpected error occurred',
      });
    }
  };
}

export function validateParams(schema: ZodSchema) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const validated = await schema.parseAsync(req.params);
      req.params = validated as any;
      next();
    } catch (error) {
      if (error instanceof ZodError) {
        return res.status(400).json({
          error: 'VALIDATION_ERROR',
          message: 'Invalid path parameters',
          details: error.issues.map((err: any) => ({
            field: err.path.join('.'),
            message: typeof err.message === 'string' && err.message.includes('Invalid option')
              ? err.message.replace('Invalid option', 'Invalid enum value')
              : err.message,
          })),
        });
      }

      return res.status(500).json({
        error: 'INTERNAL_ERROR',
        message: 'An unexpected error occurred',
      });
    }
  };
}

import { Queue, Worker, Job } from 'bullmq';
import { redisService } from '../services/redis.service';
import Redis from 'ioredis';
import { openAIWhisperService } from '../services/openai-whisper.service';
import { websocketService } from '../services/websocket.service';
import { insightService } from '../services/insight.service';
import fs from 'fs/promises';
import client from 'prom-client';

// Create BullMQ-compatible Redis connection
const bullMQConnection = new Redis({
  host: process.env.REDIS_HOST || 'localhost',
  port: parseInt(process.env.REDIS_PORT || '6379', 10),
  password: process.env.REDIS_PASSWORD || 'classwaves-redis-pass',
  maxRetriesPerRequest: null, // Required by BullMQ
});

interface TranscriptionJobData {
  audio_id: string;
  session_id: string;
  participant_id: string;
  group_id?: string;
  file_path: string;
  duration: number;
  timestamp: string;
}

// Create queue
export const transcriptionQueue = new Queue('transcription', {
  connection: bullMQConnection,
  defaultJobOptions: {
    removeOnComplete: {
      count: 100, // Keep last 100 completed jobs
      age: 24 * 3600 // Keep for 24 hours
    },
    removeOnFail: {
      count: 50, // Keep last 50 failed jobs
      age: 7 * 24 * 3600 // Keep for 7 days
    }
  }
});

// Metrics
const backpressureDrops = new client.Counter({
  name: 'ws_backpressure_drops_total',
  help: 'Total number of audio chunks dropped due to backpressure',
});

// Create worker
const transcriptionWorker = new Worker(
  'transcription',
  async (job: Job<TranscriptionJobData>) => {
    const { audio_id, session_id, participant_id, file_path } = job.data;

    console.info('Processing transcription job', { audio_id, session_id });

    try {
      // Update status to processing
      await updateAudioStatus(audio_id, 'processing');

      // Transcribe audio using OpenAI Whisper service
      const audioBuffer = await fs.readFile(file_path);
      const transcription = await openAIWhisperService.transcribeBuffer(audioBuffer, 'audio/wav');

      // Save transcription result
      const transcriptionRecord = {
        id: audio_id,
        session_id,
        participant_id,
        group_id: job.data.group_id,
        text: transcription.text,
        language: transcription.language,
        confidence: transcription.confidence,
        segments: [],
        status: 'completed',
        created_at: new Date().toISOString()
      };

      // Store in Redis
      await redisService.getClient().setex(
        `transcription:${audio_id}`,
        86400, // 24 hours
        JSON.stringify(transcriptionRecord)
      );

      // Update audio status
      await updateAudioStatus(audio_id, 'transcribed');

      // Emit real-time transcription event
      if (websocketService.io) {
        websocketService.io.to(`session:${session_id}`).emit('transcription:group:new', {
          id: audio_id,
          groupId: job.data.group_id || 'unknown',
          groupName: 'Unknown Group', // TODO: Fetch group name from database
          text: transcription.text,
          timestamp: job.data.timestamp,
          confidence: transcription.confidence || 0.95
        });
      }

      // Generate insights based on transcription (only if group_id is available)
      if (job.data.group_id) {
        const insights = await insightService.analyzeGroupTranscription({
          text: transcription.text,
          session_id,
          group_id: job.data.group_id
        });

        // Emit insights
        for (const insight of insights) {
          if (websocketService.io) {
            // Map insight types to frontend-expected types
            const mapInsightType = (type: string) => {
              switch (type) {
                case 'conceptual_density': return 'conceptual_understanding';
                case 'topical_cohesion': return 'topical_focus';
                case 'sentiment_arc': return 'collaboration_patterns';
                case 'argumentation_quality': return 'argumentation_quality';
                default: return 'argumentation_quality';
              }
            };

            websocketService.io.to(`session:${session_id}`).emit('insight:group:new', {
              groupId: job.data.group_id,
              insightType: mapInsightType(insight.type),
              message: insight.message,
              severity: insight.severity,
              timestamp: new Date().toISOString()
            });
          }
        }
      }

      // Clean up audio file
      try {
        await fs.unlink(file_path);
      } catch (err) {
        console.warn('Failed to delete audio file after transcription', { file_path, error: err });
      }

      console.info('Transcription completed', { audio_id, text_length: transcription.text.length });

      return transcriptionRecord;
    } catch (error) {
      console.error('Transcription failed', { audio_id, error });
      await updateAudioStatus(audio_id, 'failed');
      throw error;
    }
  },
  {
    connection: bullMQConnection,
    concurrency: 5, // Process up to 5 transcriptions in parallel
  }
);

// Helper functions
async function updateAudioStatus(audioId: string, status: string) {
  const recordJson = await redisService.getClient().get(`audio:${audioId}`);
  if (recordJson) {
    const record = JSON.parse(recordJson);
    record.status = status;
    await redisService.getClient().setex(`audio:${audioId}`, 86400, JSON.stringify(record));
  }
}

async function getParticipantName(participantId: string): Promise<string> {
  // Get participant info from Redis session data
  const participantJson = await redisService.getClient().get(`participant:${participantId}`);
  if (participantJson) {
    const participant = JSON.parse(participantJson);
    return participant.name || 'Unknown Student';
  }
  return 'Unknown Student';
}

// Event listeners
transcriptionWorker.on('completed', (job: Job) => {
  console.info('Transcription job completed', { jobId: job.id, audioId: job.data.audio_id });
});

transcriptionWorker.on('failed', (job: Job | undefined, err: Error) => {
  console.error('Transcription job failed', { jobId: job?.id, error: err });
});

// Export worker for graceful shutdown
export { transcriptionWorker };

