/**
 * RetryService - Phase 3 Implementation
 * 
 * Provides intelligent retry logic with:
 * - Exponential backoff with jitter
 * - Smart error classification (retryable vs non-retryable)
 * - Specialized retry strategies for different service types
 * - Comprehensive logging and metrics
 */

export interface RetryOptions {
  maxRetries: number;
  baseDelay: number;
  maxDelay: number;
  exponentialBase: number;
  jitter: boolean;
  retryCondition?: (error: any) => boolean;
  timeoutMs?: number;
}

export interface RetryContext {
  operation: string;
  attempt: number;
  maxRetries: number;
  delay: number;
  error?: Error;
}

export class RetryService {
  private static readonly DEFAULT_OPTIONS: RetryOptions = {
    maxRetries: 3,
    baseDelay: 1000,
    maxDelay: 10000,
    exponentialBase: 2,
    jitter: true,
    timeoutMs: 30000
  };

  private static metrics = {
    totalAttempts: 0,
    successfulRetries: 0,
    failedRetries: 0,
    operationCounts: new Map<string, number>()
  };

  /**
   * RELIABILITY 1: Intelligent retry with exponential backoff and jitter
   */
  static async withRetry<T>(
    operation: () => Promise<T>,
    operationName: string,
    options: Partial<RetryOptions> = {}
  ): Promise<T> {
    const config = { ...this.DEFAULT_OPTIONS, ...options };
    let lastError: Error;
    const startTime = performance.now();

    console.log(`üîÑ Starting retry operation: ${operationName} (max retries: ${config.maxRetries})`);

    for (let attempt = 0; attempt <= config.maxRetries; attempt++) {
      const context: RetryContext = {
        operation: operationName,
        attempt,
        maxRetries: config.maxRetries,
        delay: 0
      };

      try {
        this.metrics.totalAttempts++;
        this.updateOperationCount(operationName);

        // Add timeout wrapper if specified
        let result: T;
        if (config.timeoutMs) {
          result = await this.withTimeout(operation, config.timeoutMs, operationName);
        } else {
          result = await operation();
        }

        const totalTime = performance.now() - startTime;

        if (attempt > 0) {
          this.metrics.successfulRetries++;
          console.log(`‚úÖ Retry operation succeeded: ${operationName} after ${attempt} retries (${totalTime.toFixed(2)}ms)`);
        } else {
          console.log(`‚úÖ Operation succeeded on first attempt: ${operationName} (${totalTime.toFixed(2)}ms)`);
        }

        return result;

      } catch (error) {
        lastError = error as Error;
        context.error = lastError;

        if (attempt === config.maxRetries) {
          this.metrics.failedRetries++;
          const totalTime = performance.now() - startTime;
          console.error(`‚ùå Operation failed after ${config.maxRetries + 1} attempts: ${operationName} (${totalTime.toFixed(2)}ms)`, error);
          throw error;
        }

        // RELIABILITY 2: Smart retry decision
        if (config.retryCondition && !config.retryCondition(error)) {
          const errorMessage = error instanceof Error ? error.message : 'Unknown error';
          console.log(`‚è≠Ô∏è Error not retryable (custom condition), failing immediately: ${operationName}`, errorMessage);
          throw error;
        }

        if (!this.isRetryableError(error)) {
          const errorMessage = error instanceof Error ? error.message : 'Unknown error';
          console.log(`‚è≠Ô∏è Non-retryable error, failing immediately: ${operationName}`, errorMessage);
          throw error;
        }

        const delay = this.calculateDelay(attempt, config);
        context.delay = delay;

        const errorMessage = error instanceof Error ? error.message : 'Unknown error';
        console.warn(`‚ö†Ô∏è Retrying operation "${operationName}" in ${delay}ms (attempt ${attempt + 1}/${config.maxRetries + 1})`, {
          error: errorMessage,
          attempt: attempt + 1,
          delay
        });

        await this.sleep(delay);
      }
    }

    throw lastError!;
  }

  /**
   * RELIABILITY 3: Add timeout wrapper to operations
   */
  private static async withTimeout<T>(
    operation: () => Promise<T>,
    timeoutMs: number,
    operationName: string
  ): Promise<T> {
    const timeoutPromise = new Promise<never>((_, reject) => {
      setTimeout(() => {
        reject(new Error(`Operation "${operationName}" timed out after ${timeoutMs}ms`));
      }, timeoutMs);
    });

    return Promise.race([operation(), timeoutPromise]);
  }

  /**
   * RELIABILITY 4: Intelligent error classification
   */
  private static isRetryableError(error: any): boolean {
    // Network errors - always retryable
    if (error.code === 'ECONNRESET' || error.code === 'ETIMEDOUT') return true;
    if (error.code === 'ENOTFOUND' || error.code === 'ECONNREFUSED') return true;
    if (error.code === 'EHOSTUNREACH' || error.code === 'ENETUNREACH') return true;

    // HTTP errors
    if (error.status || error.response?.status) {
      const status = error.status || error.response?.status;

      // Server errors (5xx) - retryable
      if (status >= 500 && status < 600) return true;

      // Rate limiting (429) - retryable
      if (status === 429) return true;

      // Request timeout (408) - retryable
      if (status === 408) return true;

      // Service unavailable (503) - retryable
      if (status === 503) return true;

      // Bad gateway (502, 504) - retryable
      if (status === 502 || status === 504) return true;

      // Client errors (4xx except special cases) - not retryable
      if (status >= 400 && status < 500) {
        // Don't retry authentication/authorization errors
        if (status === 401 || status === 403) return false;
        // Don't retry validation errors
        if (status === 400 || status === 422) return false;
        // Don't retry not found errors
        if (status === 404) return false;
        // Other 4xx might be retryable
        return true;
      }
    }

    // Message-based detection
    const message = error.message?.toLowerCase() || '';

    // Timeout errors - retryable
    if (message.includes('timeout')) return true;
    if (message.includes('timed out')) return true;

    // Network/connection errors - retryable
    if (message.includes('network')) return true;
    if (message.includes('connection')) return true;
    if (message.includes('socket')) return true;
    if (message.includes('reset')) return true;

    // Database errors - retryable
    if (message.includes('database connection')) return true;
    if (message.includes('connection pool')) return true;
    if (message.includes('deadlock')) return true;
    if (message.includes('lock timeout')) return true;

    // Redis errors - retryable
    if (message.includes('redis connection')) return true;
    if (message.includes('redis timeout')) return true;

    // Service-specific errors
    if (message.includes('service unavailable')) return true;
    if (message.includes('temporarily unavailable')) return true;
    if (message.includes('rate limit')) return true;

    // Google OAuth specific
    if (message.includes('google')) {
      // Authentication errors are not retryable
      if (message.includes('invalid') || message.includes('unauthorized')) return false;
      // Other Google errors might be retryable
      return true;
    }

    // Circuit breaker errors - not immediately retryable
    if (message.includes('circuit breaker is open')) return false;
    if (message.includes('circuit breaker')) return false;

    // Validation/input errors - not retryable
    if (message.includes('validation')) return false;
    if (message.includes('invalid input')) return false;
    if (message.includes('malformed')) return false;

    // Default to not retryable for unknown errors
    return false;
  }

  /**
   * RELIABILITY 5: Exponential backoff with jitter
   */
  private static calculateDelay(attempt: number, options: RetryOptions): number {
    const exponentialDelay = options.baseDelay * Math.pow(options.exponentialBase, attempt);
    const delayWithCap = Math.min(exponentialDelay, options.maxDelay);

    if (options.jitter) {
      // Add random jitter to prevent thundering herd
      const jitterRange = delayWithCap * 0.1;
      const jitter = (Math.random() * 2 - 1) * jitterRange;
      return Math.max(0, delayWithCap + jitter);
    }

    return delayWithCap;
  }

  private static sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * RELIABILITY 6: Specialized retry for database operations
   */
  static async retryDatabaseOperation<T>(
    operation: () => Promise<T>,
    operationName: string
  ): Promise<T> {
    return this.withRetry(operation, `Database:${operationName}`, {
      maxRetries: 3,
      baseDelay: 500,
      maxDelay: 5000,
      timeoutMs: 30000,
      retryCondition: (error) => {
        // Don't retry authentication errors or validation errors
        if (error.status === 401 || error.status === 403) return false;
        if (error.message?.includes('validation')) return false;
        if (error.message?.includes('unauthorized')) return false;

        // Do retry connection and timeout errors
        if (error.message?.includes('connection')) return true;
        if (error.message?.includes('timeout')) return true;
        if (error.message?.includes('deadlock')) return true;

        return true;
      }
    });
  }

  /**
   * RELIABILITY 7: Specialized retry for external API calls
   */
  static async retryExternalAPI<T>(
    operation: () => Promise<T>,
    apiName: string
  ): Promise<T> {
    return this.withRetry(operation, `ExternalAPI:${apiName}`, {
      maxRetries: 2, // Fewer retries for external APIs
      baseDelay: 1000,
      maxDelay: 8000,
      timeoutMs: 15000,
      retryCondition: (error) => {
        // Don't retry client errors (4xx) except rate limiting
        const status = error.status || error.response?.status;
        if (status >= 400 && status < 500 && status !== 429) return false;

        // Don't retry authentication failures
        if (error.message?.includes('authentication') || error.message?.includes('unauthorized')) return false;

        return true;
      }
    });
  }

  /**
   * RELIABILITY 8: Specialized retry for Redis operations
   */
  static async retryRedisOperation<T>(
    operation: () => Promise<T>,
    operationName: string
  ): Promise<T> {
    return this.withRetry(operation, `Redis:${operationName}`, {
      maxRetries: 2, // Quick retries for Redis
      baseDelay: 200,
      maxDelay: 1000,
      timeoutMs: 5000,
      retryCondition: (error) => {
        // Retry connection and timeout errors
        if (error.message?.includes('connection')) return true;
        if (error.message?.includes('timeout')) return true;
        if (error.message?.includes('redis')) return true;

        // Don't retry command errors (malformed commands, etc.)
        if (error.message?.includes('wrong number of arguments')) return false;
        if (error.message?.includes('unknown command')) return false;

        return true;
      }
    });
  }

  /**
   * RELIABILITY 9: Specialized retry for Google OAuth
   */
  static async retryGoogleOAuth<T>(
    operation: () => Promise<T>,
    operationName: string
  ): Promise<T> {
    return this.withRetry(operation, `GoogleOAuth:${operationName}`, {
      maxRetries: 2,
      baseDelay: 1000,
      maxDelay: 5000,
      timeoutMs: 10000,
      retryCondition: (error) => {
        // Don't retry authentication/authorization errors
        if (error.message?.includes('invalid')) return false;
        if (error.message?.includes('unauthorized')) return false;
        if (error.message?.includes('forbidden')) return false;
        if (error.status === 401 || error.status === 403) return false;

        // Retry network and timeout errors
        if (error.message?.includes('timeout')) return true;
        if (error.message?.includes('network')) return true;
        if (error.code === 'ECONNRESET' || error.code === 'ETIMEDOUT') return true;

        return true;
      }
    });
  }

  /**
   * RELIABILITY 10: Retry with custom circuit breaker integration
   */
  static async retryWithCircuitBreaker<T>(
    operation: () => Promise<T>,
    circuitBreaker: any,
    operationName: string,
    options: Partial<RetryOptions> = {}
  ): Promise<T> {
    return this.withRetry(async () => {
      if (circuitBreaker.opened) {
        throw new Error(`Circuit breaker is open for ${operationName}`);
      }
      return await operation();
    }, operationName, {
      ...options,
      retryCondition: (error) => {
        // Don't retry if circuit breaker is open
        if (error.message?.includes('circuit breaker is open')) return false;

        // Use custom condition if provided, otherwise use default logic
        if (options.retryCondition) {
          return options.retryCondition(error);
        }

        return this.isRetryableError(error);
      }
    });
  }

  /**
   * RELIABILITY 11: Metrics and monitoring
   */
  private static updateOperationCount(operationName: string): void {
    const count = this.metrics.operationCounts.get(operationName) || 0;
    this.metrics.operationCounts.set(operationName, count + 1);
  }

  static getMetrics(): {
    totalAttempts: number;
    successfulRetries: number;
    failedRetries: number;
    successRate: number;
    retryRate: number;
    operationBreakdown: Array<{ operation: string; count: number }>;
  } {
    const totalRetries = this.metrics.successfulRetries + this.metrics.failedRetries;
    const successRate = totalRetries > 0 ? (this.metrics.successfulRetries / totalRetries) * 100 : 100;
    const retryRate = this.metrics.totalAttempts > 0 ? (totalRetries / this.metrics.totalAttempts) * 100 : 0;

    const operationBreakdown = Array.from(this.metrics.operationCounts.entries())
      .map(([operation, count]) => ({ operation, count }))
      .sort((a, b) => b.count - a.count);

    return {
      totalAttempts: this.metrics.totalAttempts,
      successfulRetries: this.metrics.successfulRetries,
      failedRetries: this.metrics.failedRetries,
      successRate: Number(successRate.toFixed(2)),
      retryRate: Number(retryRate.toFixed(2)),
      operationBreakdown
    };
  }

  /**
   * RELIABILITY 12: Reset metrics (useful for testing or periodic resets)
   */
  static resetMetrics(): void {
    this.metrics = {
      totalAttempts: 0,
      successfulRetries: 0,
      failedRetries: 0,
      operationCounts: new Map<string, number>()
    };
  }

  /**
   * RELIABILITY 13: Batch retry operations
   */
  static async retryBatch<T>(
    operations: Array<{ operation: () => Promise<T>; name: string; options?: Partial<RetryOptions> }>,
    concurrency: number = 5
  ): Promise<Array<{ success: boolean; result?: T; error?: Error; name: string }>> {
    console.log(`üîÑ Starting batch retry operations: ${operations.length} operations, concurrency: ${concurrency}`);

    const results: Array<{ success: boolean; result?: T; error?: Error; name: string }> = [];

    // Process operations in batches to control concurrency
    for (let i = 0; i < operations.length; i += concurrency) {
      const batch = operations.slice(i, i + concurrency);

      const batchResults = await Promise.allSettled(
        batch.map(async ({ operation, name, options }) => {
          try {
            const result = await this.withRetry(operation, name, options);
            return { success: true, result, name };
          } catch (error) {
            return { success: false, error: error as Error, name };
          }
        })
      );

      results.push(...batchResults.map(result => 
        result.status === 'fulfilled' ? result.value : 
        { success: false, error: result.reason, name: 'unknown' }
      ));
    }

    const successCount = results.filter(r => r.success).length;
    console.log(`‚úÖ Batch retry operations completed: ${successCount}/${operations.length} successful`);

    return results;
  }

  /**
   * RELIABILITY 14: Health check for retry service
   */
  static getHealthStatus(): {
    status: 'healthy' | 'degraded' | 'unhealthy';
    metrics: ReturnType<typeof RetryService.getMetrics>;
    recommendations: string[];
  } {
    const metrics = this.getMetrics();
    const recommendations: string[] = [];

    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';

    // Analyze retry patterns for health
    if (metrics.retryRate > 50) {
      status = 'degraded';
      recommendations.push('High retry rate detected - investigate underlying service issues');
    }

    if (metrics.successRate < 80) {
      status = 'unhealthy';
      recommendations.push('Low retry success rate - services may be consistently failing');
    }

    if (metrics.totalAttempts === 0) {
      recommendations.push('No retry operations recorded - system may not be under load');
    }

    // Check for problematic operations
    const topFailingOperations = metrics.operationBreakdown
      .filter(op => op.count > 10)
      .slice(0, 3);

    if (topFailingOperations.length > 0) {
      recommendations.push(`High retry operations: ${topFailingOperations.map(op => op.operation).join(', ')}`);
    }

    return {
      status,
      metrics,
      recommendations
    };
  }
}

import * as jwt from 'jsonwebtoken';
import * as crypto from 'crypto';
import { Request } from 'express';
import { Teacher, School } from '../types/auth.types';
import { redisService } from './redis.service';
import { JWTConfigService } from '../config/jwt.config';

interface SecureJWTPayload {
  userId: string;
  email: string;
  schoolId: string;
  sessionId: string;
  fingerprint: string;
  type: 'access' | 'refresh';
  role: string;
  iat: number;
  exp: number;
  jti: string; // JWT ID for anti-replay
}

interface StudentJWTPayload extends Omit<SecureJWTPayload, 'email' | 'schoolId' | 'fingerprint'> {
  studentId: string;
  groupId: string;
  sessionCode: string;
}

interface TokenPair {
  accessToken: string;
  refreshToken: string;
  deviceFingerprint: string;
  expiresIn: number;
  refreshExpiresIn: number;
}

/**
 * SecureJWTService - Enhanced JWT security with device fingerprinting
 * 
 * Features:
 * - Device fingerprinting to prevent token theft
 * - Short-lived access tokens (15 minutes)
 * - Long-lived refresh tokens (7 days)
 * - Anti-replay protection with JWT IDs
 * - Redis-based token blacklist for immediate revocation
 * - Comprehensive token verification
 */
export class SecureJWTService {
  private static readonly FINGERPRINT_ALGORITHM = 'sha256';
  private static readonly ACCESS_TOKEN_TTL = 15 * 60; // 15 minutes
  private static readonly REFRESH_TOKEN_TTL = 7 * 24 * 60 * 60; // 7 days
  private static readonly BLACKLIST_PREFIX = 'blacklist:';
  private static readonly BLACKLIST_CLEANUP_INTERVAL = 24 * 60 * 60 * 1000; // 24 hours

  // Centralized JWT configuration service
  private static readonly jwtConfig = JWTConfigService.getInstance();

    // SECURITY 1: Device fingerprinting to prevent token theft
static createDeviceFingerprint(req: Request): string {
    // Deterministic override for tests to avoid env-dependent IP/UA mismatches
    if (process.env.NODE_ENV === 'test') {
      const override = (req.headers['x-cw-fingerprint'] as string) || '';
      if (override) {
        try {
          return crypto
            .createHash(this.FINGERPRINT_ALGORITHM)
            .update(`test-override:${override}`)
            .digest('hex')
            .substring(0, 16);
        } catch {
          // fall through to normal path
        }
      }
    }
    console.log('üîß DEBUG: Starting device fingerprint creation');

    const userAgent = req.headers['user-agent'] || '';
    const ip = req.ip || '';

    console.log('üîß DEBUG: Fingerprint components:', {
      userAgent: userAgent,
      ip: ip,
      'x-forwarded-for': req.headers['x-forwarded-for'],
      'x-real-ip': req.headers['x-real-ip']
    });

    // Handle potential CI environment issues
    if (!userAgent && !ip) {
      console.log('üîß DEBUG: WARNING - Both user-agent and IP are missing, using fallback');
      const fallbackFingerprint = crypto
        .createHash(this.FINGERPRINT_ALGORITHM)
        .update('ci-environment-fallback')
        .digest('hex')
        .substring(0, 16);
      console.log('üîß DEBUG: Fallback fingerprint created:', fallbackFingerprint);
      return fallbackFingerprint;
    }

    const components = [userAgent, ip];
    console.log('üîß DEBUG: Components array:', components);

    const fingerprint = crypto
      .createHash(this.FINGERPRINT_ALGORITHM)
      .update(components.join('|'))
      .digest('hex')
      .substring(0, 16); // First 16 chars for storage efficiency

    console.log('üîß DEBUG: Device fingerprint created:', fingerprint);
    return fingerprint;
  }

  // SECURITY 2: Generate secure token pair with short-lived access tokens
  static async generateSecureTokens(
    teacher: Teacher, 
    school: School, 
    sessionId: string, 
    req: Request
  ): Promise<TokenPair> {
    console.log('üîß DEBUG: Starting SecureJWTService.generateSecureTokens');
    console.log('üîß DEBUG: Input parameters:', {
      teacherId: teacher.id,
      schoolId: school.id,
      sessionId: sessionId,
      requestHeaders: {
        'user-agent': req.headers['user-agent'],
        'x-forwarded-for': req.headers['x-forwarded-for'],
        'ip': req.ip
      }
    });

    try {
      console.log('üîß DEBUG: Creating device fingerprint');
      const deviceFingerprint = this.createDeviceFingerprint(req);
      console.log('üîß DEBUG: Device fingerprint created:', deviceFingerprint);

      const now = Math.floor(Date.now() / 1000);
      console.log('üîß DEBUG: Current timestamp:', now);

      const basePayload = {
        userId: teacher.id,
        email: teacher.email,
        schoolId: school.id,
        sessionId,
        fingerprint: deviceFingerprint,
        role: teacher.role,
        iat: now
      };
      console.log('üîß DEBUG: Base payload created:', basePayload);

      // Generate unique JTIs for anti-replay protection
      console.log('üîß DEBUG: Generating JTIs');
      const accessJti = crypto.randomUUID();
      const refreshJti = crypto.randomUUID();
      console.log('üîß DEBUG: JTIs generated:', { accessJti, refreshJti });

      // Check JWT secrets
      console.log('üîß DEBUG: Checking JWT secrets availability');
      const jwtSecret = process.env.JWT_SECRET;
      const jwtRefreshSecret = process.env.JWT_REFRESH_SECRET;
      console.log('üîß DEBUG: JWT_SECRET present:', !!jwtSecret);
      console.log('üîß DEBUG: JWT_REFRESH_SECRET present:', !!jwtRefreshSecret);

      if (!jwtSecret || !jwtRefreshSecret) {
        throw new Error('JWT secrets not available');
      }

      // Short-lived access token (15 minutes) - Use centralized JWT configuration
      console.log('üîß DEBUG: Signing access token');
      const accessSigningKey = SecureJWTService.jwtConfig.getSigningKey();
      const accessAlgorithm = SecureJWTService.jwtConfig.getAlgorithm();
      console.log('üîß DEBUG: Using algorithm:', accessAlgorithm);

      const accessToken = jwt.sign({
        ...basePayload,
        type: 'access',
        exp: now + this.ACCESS_TOKEN_TTL,
        jti: accessJti
      }, accessSigningKey, {
        algorithm: accessAlgorithm
      });
      console.log('üîß DEBUG: Access token signed successfully');

      // Longer-lived refresh token (7 days) - Use HS256 with refresh secret per auth design
      console.log('üîß DEBUG: Signing refresh token');
      const refreshToken = jwt.sign({
        ...basePayload,
        type: 'refresh',
        exp: now + this.REFRESH_TOKEN_TTL,
        jti: refreshJti
      }, jwtRefreshSecret || SecureJWTService.jwtConfig.getJWTSecret(), {
        algorithm: 'HS256'
      });
      console.log('üîß DEBUG: Refresh token signed successfully');

      // Store token metadata for tracking and revocation
      console.log('üîß DEBUG: Storing token metadata');
      await this.storeTokenMetadata(accessJti, refreshJti, teacher.id, sessionId);
      console.log('üîß DEBUG: Token metadata stored successfully');

      console.log(`üîê Generated secure tokens for user ${teacher.id} - Access: ${this.ACCESS_TOKEN_TTL}s, Refresh: ${this.REFRESH_TOKEN_TTL}s`);

      const result = { 
        accessToken, 
        refreshToken, 
        deviceFingerprint,
        expiresIn: this.ACCESS_TOKEN_TTL,
        refreshExpiresIn: this.REFRESH_TOKEN_TTL
      };

      console.log('üîß DEBUG: SecureJWTService.generateSecureTokens completed successfully');
      return result;

    } catch (error) {
      console.error('üîß DEBUG: ERROR in SecureJWTService.generateSecureTokens:', error);
      console.error('üîß DEBUG: JWT Generation error details:', {
        message: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : 'No stack trace',
        name: error instanceof Error ? error.name : 'Unknown'
      });
      throw error;
    }
  }

  // SECURITY 3: Comprehensive token verification
  static async verifyTokenSecurity(
    token: string, 
    req: Request, 
    tokenType: 'access' | 'refresh' = 'access'
  ): Promise<SecureJWTPayload | null> {
    try {
      // Use centralized JWT configuration for verification
      const verificationKey = tokenType === 'access' 
        ? SecureJWTService.jwtConfig.getVerificationKey()
        : (process.env.JWT_REFRESH_SECRET! || SecureJWTService.jwtConfig.getJWTSecret());

      const algorithm = tokenType === 'access'
        ? SecureJWTService.jwtConfig.getAlgorithm()
        : 'HS256'; // Refresh tokens always use HS256

      const payload = jwt.verify(token, verificationKey, {
        algorithms: [algorithm]
      }) as SecureJWTPayload;

      // SECURITY 4: Verify token type matches expected
      if (payload.type !== tokenType) {
        console.warn(`üö® Token type mismatch: expected ${tokenType}, got ${payload.type}`);
        return null;
      }

      // SECURITY 5: Verify device fingerprint
      const currentFingerprint = this.createDeviceFingerprint(req);
      if (payload.fingerprint !== currentFingerprint) {
        console.warn(`üö® Device fingerprint mismatch for user ${payload.userId}:`, {
          expected: payload.fingerprint,
          actual: currentFingerprint,
          userAgent: req.headers['user-agent'],
          ip: req.ip
        });

        // Log suspicious activity for monitoring
        await this.logSuspiciousActivity(payload.userId, 'fingerprint_mismatch', req);
        return null;
      }

      // SECURITY 6: Check token blacklist (for logout/revocation)
      const isBlacklisted = await this.isTokenBlacklisted(payload.jti);
      if (isBlacklisted) {
        console.warn(`üö® Blacklisted token attempted: ${payload.jti} for user ${payload.userId}`);
        return null;
      }

      // SECURITY 7: Verify token hasn't been replayed (check unique usage)
      const replayKey = `replay:${payload.jti}`;
      const hasBeenUsed = await redisService.get(replayKey);
      if (hasBeenUsed && tokenType === 'refresh') {
        console.warn(`üö® Token replay detected: ${payload.jti} for user ${payload.userId}`);
        await this.logSuspiciousActivity(payload.userId, 'token_replay', req);
        return null;
      }

      // Mark refresh token as used to prevent replay
      if (payload.type === 'refresh') {
        await redisService.set(replayKey, 'used', this.ACCESS_TOKEN_TTL);
      }

      return payload;
    } catch (error) {
      const errorType = error instanceof jwt.TokenExpiredError ? 'expired' : 
                       error instanceof jwt.JsonWebTokenError ? 'invalid' : 'unknown';

      console.warn(`üö® Token verification failed (${errorType}):`, error instanceof Error ? error.message : 'Unknown error');
      return null;
    }
  }

  static async generateStudentToken(
    studentId: string,
    sessionId: string,
    groupId: string,
    sessionCode: string,
  ): Promise<string> {
    const jti = crypto.randomBytes(16).toString('hex');
    const now = Math.floor(Date.now() / 1000);

    const payload: Omit<StudentJWTPayload, 'iat' | 'exp' | 'jti'> = {
      userId: studentId, // For consistency with teacher payload
      studentId,
      sessionId,
      groupId,
      sessionCode,
      type: 'access',
      role: 'student',
    };

    // Generate token using centralized JWT configuration
    const accessToken = jwt.sign(
      { ...payload, jti },
      SecureJWTService.jwtConfig.getSigningKey(),
      {
        expiresIn: this.ACCESS_TOKEN_TTL,
        algorithm: SecureJWTService.jwtConfig.getAlgorithm(),
        // The 'iat' (issued at) claim is automatically added by the library
      }
    );

    return accessToken;
  }

  // REMOVED: getSigningKey() and getAlgorithm() methods
  // Now using centralized JWTConfigService for consistent algorithm detection and key management

  // SECURITY 8: Token rotation for enhanced security
  static async rotateTokens(
    refreshToken: string,
    req: Request
  ): Promise<TokenPair | null> {
    try {
      // Verify the refresh token
      const payload = await this.verifyTokenSecurity(refreshToken, req, 'refresh');
      if (!payload) {
        console.warn(`üö® Invalid refresh token used for rotation`);
        return null;
      }

      // Get teacher and school data for new tokens
      const [teacher, school] = await Promise.all([
        this.getTeacherById(payload.userId),
        this.getSchoolById(payload.schoolId)
      ]);

      if (!teacher || !school) {
        console.warn(`üö® Teacher or school not found for token rotation: ${payload.userId}`);
        return null;
      }

      // Blacklist the old refresh token to prevent reuse
      await this.revokeToken(payload.jti, 'Token rotation');

      // Generate new token pair
      const newTokens = await this.generateSecureTokens(teacher, school, payload.sessionId, req);

      // CRITICAL: Update session data in Redis to sync with new device fingerprint
      // This prevents auth middleware fallback issues when session cookie is used
      try {
        const { SecureSessionService } = await import('./secure-session.service');
        console.log('üîÑ Updating Redis session data with new device fingerprint...');

        // Update existing session with new device fingerprint and activity timestamp
        await SecureSessionService.updateSessionOnRotation(
          payload.sessionId, 
          newTokens.deviceFingerprint, 
          req
        );

        console.log(`‚úÖ Session ${payload.sessionId} updated with new device fingerprint`);
      } catch (sessionError) {
        console.error('‚ùå Failed to update session data during token rotation:', sessionError);
        // Don't fail the entire rotation, but log the issue for monitoring
        // The tokens are still valid, just the session fallback might have issues
      }

      console.log(`üîÑ Token rotation successful for user ${teacher.id}`);
      return newTokens;
    } catch (error) {
      console.error(`‚ùå Token rotation failed:`, error);
      return null;
    }
  }

  // SECURITY 9: Token revocation for logout and security incidents
  static async revokeToken(jti: string, reason: string = 'Manual revocation'): Promise<void> {
    const blacklistKey = `${this.BLACKLIST_PREFIX}${jti}`;
    const blacklistData = {
      revokedAt: new Date().toISOString(),
      reason,
      timestamp: Date.now()
    };

    // Store in blacklist with TTL equal to max token lifetime
    await redisService.set(blacklistKey, JSON.stringify(blacklistData), this.REFRESH_TOKEN_TTL);

    console.log(`üö´ Token revoked: ${jti} - Reason: ${reason}`);
  }

  // SECURITY 10: Check if token is blacklisted
  static async isTokenBlacklisted(jti: string): Promise<boolean> {
    const blacklistKey = `${this.BLACKLIST_PREFIX}${jti}`;
    const blacklistedData = await redisService.get(blacklistKey);
    return blacklistedData !== null;
  }

  // SECURITY 11: Revoke all tokens for a user (security incident response)
  static async revokeAllUserTokens(
    userId: string, 
    reason: string = 'Security incident'
  ): Promise<void> {
    try {
      // Get all active sessions for the user
      const activeSessions = await redisService.getTeacherActiveSessions(userId);

      // Get token metadata for all sessions
      const tokenMetadataKeys = activeSessions.map(sessionId => `tokens:${sessionId}`);
      const tokenMetadataList = await Promise.all(
        tokenMetadataKeys.map(key => redisService.get(key))
      );

      // Revoke all tokens
      const revocationPromises: Promise<void>[] = [];
      for (const metadata of tokenMetadataList) {
        if (metadata) {
          const { accessJti, refreshJti } = JSON.parse(metadata);
          revocationPromises.push(
            this.revokeToken(accessJti, reason),
            this.revokeToken(refreshJti, reason)
          );
        }
      }

      await Promise.all(revocationPromises);

      // Invalidate all sessions
      await redisService.invalidateAllTeacherSessions(userId);

      console.log(`üö´ All tokens revoked for user ${userId} - Reason: ${reason}`);
    } catch (error) {
      console.error(`‚ùå Failed to revoke all tokens for user ${userId}:`, error);
      throw error;
    }
  }

  // SECURITY 12: Store token metadata for tracking
  private static async storeTokenMetadata(
    accessJti: string,
    refreshJti: string,
    userId: string,
    sessionId: string
  ): Promise<void> {
    const metadata = {
      accessJti,
      refreshJti,
      userId,
      sessionId,
      createdAt: new Date().toISOString()
    };

    // Store with session expiration
    await redisService.set(
      `tokens:${sessionId}`, 
      JSON.stringify(metadata), 
      this.REFRESH_TOKEN_TTL
    );
  }

  // SECURITY 13: Log suspicious activity for monitoring
  private static async logSuspiciousActivity(
    userId: string,
    activityType: string,
    req: Request
  ): Promise<void> {
    const suspiciousActivity = {
      userId,
      activityType,
      timestamp: new Date().toISOString(),
      ip: req.ip,
      userAgent: req.headers['user-agent'],
      headers: {
        'x-forwarded-for': req.headers['x-forwarded-for'],
        'accept-language': req.headers['accept-language']
      }
    };

    // Store in Redis for security monitoring
    const key = `suspicious:${userId}:${Date.now()}`;
    await redisService.set(key, JSON.stringify(suspiciousActivity), 86400); // 24 hours

    console.warn(`üö® SUSPICIOUS ACTIVITY LOGGED:`, suspiciousActivity);

    // In production: send to security monitoring system
    // await securityMonitoringService.alert(suspiciousActivity);
  }

  // Helper methods for database queries
  private static async getTeacherById(teacherId: string): Promise<Teacher | null> {
    try {
      // Import databricks service dynamically to avoid circular dependencies
      const { databricksService } = await import('./databricks.service');
      return await databricksService.queryOne<Teacher>(
        `SELECT id, email, name, school_id, role, status FROM classwaves.users.teachers WHERE id = ? AND status = 'active'`,
        [teacherId]
      );
    } catch (error) {
      console.error(`‚ùå Failed to get teacher ${teacherId}:`, error);
      return null;
    }
  }

  private static async getSchoolById(schoolId: string): Promise<School | null> {
    try {
      const { databricksService } = await import('./databricks.service');
      return await databricksService.queryOne<School>(
        `SELECT id, name, domain, subscription_status, subscription_tier, ferpa_agreement, coppa_compliant FROM classwaves.users.schools WHERE id = ? AND subscription_status IN ('active', 'trial')`,
        [schoolId]
      );
    } catch (error) {
      console.error(`‚ùå Failed to get school ${schoolId}:`, error);
      return null;
    }
  }

  // SECURITY 14: Periodic cleanup of blacklisted tokens
  static startBlacklistCleanup(): void {
    setInterval(async () => {
      try {
        console.log('üßπ Starting blacklist cleanup...');

        // Get all blacklist keys
        const pattern = `${this.BLACKLIST_PREFIX}*`;
        const blacklistKeys = await redisService.getClient().keys(pattern);

        let cleanupCount = 0;
        for (const key of blacklistKeys) {
          const ttl = await redisService.getClient().ttl(key);
          if (ttl <= 0) {
            await redisService.getClient().del(key);
            cleanupCount++;
          }
        }

        if (cleanupCount > 0) {
          console.log(`üßπ Cleaned up ${cleanupCount} expired blacklist entries`);
        }
      } catch (error) {
        console.error('‚ùå Blacklist cleanup failed:', error);
      }
    }, this.BLACKLIST_CLEANUP_INTERVAL);
  }

  // SECURITY 15: Get security metrics for monitoring
  static async getSecurityMetrics(): Promise<{
    blacklistedTokens: number;
    suspiciousActivities: number;
    activeTokens: number;
  }> {
    try {
      const [blacklistKeys, suspiciousKeys, tokenKeys] = await Promise.all([
        redisService.getClient().keys(`${this.BLACKLIST_PREFIX}*`),
        redisService.getClient().keys('suspicious:*'),
        redisService.getClient().keys('tokens:*')
      ]);

      return {
        blacklistedTokens: blacklistKeys.length,
        suspiciousActivities: suspiciousKeys.length,
        activeTokens: tokenKeys.length
      };
    } catch (error) {
      console.error('‚ùå Failed to get security metrics:', error);
      return { blacklistedTokens: 0, suspiciousActivities: 0, activeTokens: 0 };
    }
  }
}

// Start blacklist cleanup on service initialization
if (process.env.NODE_ENV !== 'test') {
  SecureJWTService.startBlacklistCleanup();
}

import * as crypto from 'crypto';
import { Request } from 'express';
import { redisService } from './redis.service';
import { Teacher, School } from '../types/auth.types';
import { SecureJWTService } from './secure-jwt.service';

interface SecureSessionData {
  teacherId: string;
  teacher: Teacher;
  school: School;
  sessionId: string;
  deviceFingerprint: string;
  ipAddress: string;
  userAgent: string;
  createdAt: Date;
  lastActivity: Date;
  loginAttempts?: number;
  isSuspicious?: boolean;
  geoLocation?: {
    country?: string;
    region?: string;
    city?: string;
  };
}

interface EncryptedSessionWrapper {
  iv: string;
  data: string;
  authTag: string;
  algorithm: string;
  sessionId: string; // Needed for AAD verification
}

interface LoginAttemptMetrics {
  attempts: number;
  firstAttempt: Date;
  lastAttempt: Date;
  suspiciousIPs: string[];
}

/**
 * SecureSessionService - Enhanced session management with encryption and security monitoring
 * 
 * Features:
 * - AES-256-GCM encryption for all sensitive session data
 * - Concurrent session limits enforcement (max 3 per user)
 * - Suspicious login pattern detection and alerting
 * - Session fingerprinting and validation
 * - Automatic session cleanup and security monitoring
 * - Geographic location tracking for anomaly detection
 */
export class SecureSessionService {
  private static readonly ENCRYPTION_ALGORITHM = 'aes-256-gcm';
  private static readonly ENCRYPTION_KEY_LENGTH = 32;
  private static readonly IV_LENGTH = 16;
  private static readonly AUTH_TAG_LENGTH = 16;
  private static readonly MAX_CONCURRENT_SESSIONS = 3;
  private static readonly SUSPICIOUS_LOGIN_THRESHOLD = 5;
  private static readonly SESSION_ACTIVITY_WINDOW = 5 * 60 * 1000; // 5 minutes
  private static readonly CLEANUP_INTERVAL = 60 * 60 * 1000; // 1 hour

  // Derive encryption key from environment secret
  private static readonly ENCRYPTION_KEY = crypto.scryptSync(
    process.env.SESSION_ENCRYPTION_SECRET || 'fallback-insecure-key-replace-in-production', 
    'classwaves-session-salt', 
    SecureSessionService.ENCRYPTION_KEY_LENGTH
  );

  // SECURITY 1: Encrypt sensitive session data using AES-256-GCM
  private static encryptSessionData(data: SecureSessionData): string {
    try {
      const iv = crypto.randomBytes(this.IV_LENGTH);
      const cipher = crypto.createCipheriv(this.ENCRYPTION_ALGORITHM, this.ENCRYPTION_KEY, iv);
      cipher.setAAD(Buffer.from(data.sessionId)); // Additional authenticated data

      const serialized = JSON.stringify({
        ...data,
        createdAt: data.createdAt.toISOString(),
        lastActivity: data.lastActivity.toISOString()
      });

      let encrypted = cipher.update(serialized, 'utf8', 'hex');
      encrypted += cipher.final('hex');

      const authTag = cipher.getAuthTag();

      const wrapper: EncryptedSessionWrapper = {
        iv: iv.toString('hex'),
        data: encrypted,
        authTag: authTag.toString('hex'),
        algorithm: this.ENCRYPTION_ALGORITHM,
        sessionId: data.sessionId
      };

      return JSON.stringify(wrapper);
    } catch (error) {
      console.error('‚ùå Session encryption failed:', error);
      throw new Error('Failed to encrypt session data');
    }
  }

  // SECURITY 2: Decrypt and verify session data integrity
  private static decryptSessionData(encryptedData: string): SecureSessionData | null {
    try {
      const wrapper: EncryptedSessionWrapper = JSON.parse(encryptedData);

      // Verify algorithm matches expected
      if (wrapper.algorithm !== this.ENCRYPTION_ALGORITHM) {
        console.error('üö® Session decryption failed: algorithm mismatch');
        return null;
      }

      const decipher = crypto.createDecipheriv(this.ENCRYPTION_ALGORITHM, this.ENCRYPTION_KEY, Buffer.from(wrapper.iv, 'hex'));
      decipher.setAAD(Buffer.from(wrapper.sessionId)); // Use actual sessionId for AAD
      decipher.setAuthTag(Buffer.from(wrapper.authTag, 'hex'));

      let decrypted = decipher.update(wrapper.data, 'hex', 'utf8');
      decrypted += decipher.final('utf8');

      const sessionData = JSON.parse(decrypted);

      return {
        ...sessionData,
        createdAt: new Date(sessionData.createdAt),
        lastActivity: new Date(sessionData.lastActivity)
      };
    } catch (error) {
      console.error('üö® Session decryption failed:', error);
      return null;
    }
  }

  // SECURITY 3: Store session with comprehensive security features
  static async storeSecureSession(
    sessionId: string, 
    teacher: Teacher, 
    school: School, 
    req: Request
  ): Promise<void> {
    console.log('üîß DEBUG: Starting SecureSessionService.storeSecureSession');
    console.log('üîß DEBUG: Session storage input:', {
      sessionId,
      teacherId: teacher.id,
      schoolId: school.id,
      requestIP: req.ip,
      userAgent: req.headers['user-agent']
    });

    const storeStart = performance.now();

    try {
      // SECURITY 4: Enforce concurrent session limits
      console.log('üîß DEBUG: Enforcing session limits');
      await this.enforceSessionLimits(teacher.id);
      console.log('üîß DEBUG: Session limits enforced successfully');

      // SECURITY 5: Track and analyze login patterns
      console.log('üîß DEBUG: Tracking login metrics');
      const isFirstLogin = await this.trackLoginMetrics(teacher.id, req.ip!);
      console.log('üîß DEBUG: Login metrics tracked - First login:', isFirstLogin);

      // SECURITY 6: Detect suspicious login patterns
      console.log('üîß DEBUG: Detecting suspicious activity');
      const isSuspicious = await this.detectSuspiciousActivity(teacher.id, req);
      console.log('üîß DEBUG: Suspicious activity check completed - Suspicious:', isSuspicious);

      console.log('üîß DEBUG: Creating session data object');
      const sessionData: SecureSessionData = {
        teacherId: teacher.id,
        teacher,
        school,
        sessionId,
        deviceFingerprint: SecureJWTService.createDeviceFingerprint(req),
        ipAddress: req.ip!,
        userAgent: req.headers['user-agent'] || '',
        createdAt: new Date(),
        lastActivity: new Date(),
        isSuspicious
      };
      console.log('üîß DEBUG: Session data object created');

      // Add geographic information if available
      if (req.headers['cf-ipcountry']) {
        console.log('üîß DEBUG: Adding geographic information');
        sessionData.geoLocation = {
          country: req.headers['cf-ipcountry'] as string,
          region: req.headers['cf-ipregion'] as string,
          city: req.headers['cf-ipcity'] as string
        };
      } else {
        console.log('üîß DEBUG: No geographic information available');
      }

      console.log('üîß DEBUG: Encrypting session data');
      const encryptedData = this.encryptSessionData(sessionData);
      console.log('üîß DEBUG: Session data encrypted successfully');

      // Store encrypted session with sliding expiration
      console.log('üîß DEBUG: Storing encrypted session in Redis');
      const sessionTTL = 24 * 60 * 60; // 24 hours
      await redisService.set(`secure_session:${sessionId}`, encryptedData, sessionTTL);
      console.log('üîß DEBUG: Encrypted session stored in Redis successfully');

      // Track active sessions for the teacher
      console.log('üîß DEBUG: Adding session to teacher active sessions set');
      await redisService.getClient().sadd(`teacher_sessions:${teacher.id}`, sessionId);
      await redisService.getClient().expire(`teacher_sessions:${teacher.id}`, 86400); // 24 hours
      console.log('üîß DEBUG: Session added to teacher active sessions set');

      // Store session metadata for monitoring
      console.log('üîß DEBUG: Storing session metadata');
      await this.storeSessionMetadata(sessionId, teacher.id, req);
      console.log('üîß DEBUG: Session metadata stored successfully');

      const storeTime = performance.now() - storeStart;
      console.log(`üîí Secure session stored: ${sessionId} (${storeTime.toFixed(2)}ms) - Suspicious: ${isSuspicious}`);

      // Alert if suspicious activity detected
      if (isSuspicious) {
        console.log('üîß DEBUG: Alerting suspicious session');
        await this.alertSuspiciousSession(teacher.id, sessionId, req);
        console.log('üîß DEBUG: Suspicious session alert sent');
      }

      console.log('üîß DEBUG: SecureSessionService.storeSecureSession completed successfully');

    } catch (error) {
      console.error('üîß DEBUG: ERROR in SecureSessionService.storeSecureSession:', error);
      console.error('üîß DEBUG: Session storage error details:', {
        message: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : 'No stack trace',
        name: error instanceof Error ? error.name : 'Unknown'
      });
      console.error(`‚ùå Secure session storage failed for ${sessionId}:`, error);
      throw error;
    }
  }

  // SECURITY 6.5: Update session data during token rotation
  static async updateSessionOnRotation(
    sessionId: string,
    newDeviceFingerprint: string,
    req: Request
  ): Promise<void> {
    console.log('üîÑ Starting session update for token rotation');

    try {
      // First retrieve the existing session data
      const existingSessionData = await this.getSecureSession(sessionId, req);

      if (!existingSessionData) {
        console.warn(`‚ö†Ô∏è Session ${sessionId} not found during rotation - may have expired`);
        return;
      }

      // Update session data with new device fingerprint and activity timestamp
      const updatedSessionData: SecureSessionData = {
        ...existingSessionData,
        deviceFingerprint: newDeviceFingerprint,
        lastActivity: new Date(),
        // Clear any suspicious flags since this is a valid rotation
        isSuspicious: false
      };

      console.log('üîÑ Encrypting updated session data...');
      const encryptedSessionData = this.encryptSessionData(updatedSessionData);

      // Store the updated session with same TTL as original
      const sessionTtl = 86400; // 24 hours in seconds (SESSION_TTL equivalent)
      await redisService.set(
        `secure_session:${sessionId}`,
        JSON.stringify(encryptedSessionData),
        sessionTtl
      );

      console.log(`‚úÖ Session ${sessionId} successfully updated with new device fingerprint`);

    } catch (error) {
      console.error(`‚ùå Failed to update session during token rotation for ${sessionId}:`, error);
      throw new Error(`Session update failed during token rotation: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  // SECURITY 7: Retrieve and verify secure session
  static async getSecureSession(sessionId: string, req?: Request): Promise<SecureSessionData | null> {
    const retrieveStart = performance.now();

    try {
      const encryptedData = await redisService.get(`secure_session:${sessionId}`);

      if (!encryptedData) {
        console.log(`‚ÑπÔ∏è  Session not found: ${sessionId}`);
        return null;
      }

      const sessionData = this.decryptSessionData(encryptedData);

      if (!sessionData) {
        console.error(`üö® Failed to decrypt session: ${sessionId}`);
        await this.removeCorruptedSession(sessionId);
        return null;
      }

      // SECURITY 8: Validate session integrity
      if (req) {
        const isValid = await this.validateSessionIntegrity(sessionData, req);
        if (!isValid) {
          console.warn(`üö® Session integrity validation failed: ${sessionId}`);
          await this.invalidateSession(sessionId, 'Integrity validation failed');
          return null;
        }
      }

      // Update last activity
      sessionData.lastActivity = new Date();
      const updatedEncryptedData = this.encryptSessionData(sessionData);
      await redisService.set(`secure_session:${sessionId}`, updatedEncryptedData, 24 * 60 * 60);

      const retrieveTime = performance.now() - retrieveStart;
      console.log(`üîì Secure session retrieved: ${sessionId} (${retrieveTime.toFixed(2)}ms)`);

      return sessionData;
    } catch (error) {
      console.error(`‚ùå Secure session retrieval failed for ${sessionId}:`, error);
      return null;
    }
  }

  // SECURITY 9: Enforce concurrent session limits
  private static async enforceSessionLimits(teacherId: string): Promise<void> {
    try {
      const activeSessions = await redisService.getClient().smembers(`teacher_sessions:${teacherId}`);

      // Remove expired sessions from the set
      const validSessions: string[] = [];
      for (const sessionId of activeSessions) {
        const exists = await redisService.getClient().exists(`secure_session:${sessionId}`);
        if (exists) {
          validSessions.push(sessionId);
        }
      }

      // Update the set with only valid sessions
      if (validSessions.length !== activeSessions.length) {
        await redisService.getClient().del(`teacher_sessions:${teacherId}`);
        if (validSessions.length > 0) {
          await redisService.getClient().sadd(`teacher_sessions:${teacherId}`, ...validSessions);
          await redisService.getClient().expire(`teacher_sessions:${teacherId}`, 86400);
        }
      }

      // Enforce limit by removing oldest sessions
      if (validSessions.length >= this.MAX_CONCURRENT_SESSIONS) {
        // Get creation times to determine oldest sessions
        const sessionTimes: { sessionId: string; createdAt: Date }[] = [];

        for (const sessionId of validSessions) {
          const sessionData = await this.getSecureSession(sessionId);
          if (sessionData) {
            sessionTimes.push({ sessionId, createdAt: sessionData.createdAt });
          }
        }

        // Sort by creation time (oldest first)
        sessionTimes.sort((a, b) => a.createdAt.getTime() - b.createdAt.getTime());

        // Remove oldest sessions to make room for new one
        const sessionsToRemove = sessionTimes.slice(0, validSessions.length - this.MAX_CONCURRENT_SESSIONS + 1);

        for (const { sessionId } of sessionsToRemove) {
          await this.invalidateSession(sessionId, 'Concurrent session limit exceeded');
          await redisService.getClient().srem(`teacher_sessions:${teacherId}`, sessionId);
        }

        console.log(`üîí Enforced session limit for teacher ${teacherId}, removed ${sessionsToRemove.length} sessions`);
      }
    } catch (error) {
      console.error(`‚ùå Session limit enforcement failed for teacher ${teacherId}:`, error);
    }
  }

  // SECURITY 10: Track and analyze login metrics for suspicious activity detection
  private static async trackLoginMetrics(teacherId: string, ipAddress: string): Promise<boolean> {
    try {
      const timeWindow = Math.floor(Date.now() / (5 * 60 * 1000)); // 5-minute windows
      const ipKey = `login_attempts:${ipAddress}:${timeWindow}`;
      const teacherKey = `teacher_logins:${teacherId}:${timeWindow}`;

      // Track IP-based attempts
      const ipAttempts = await redisService.getClient().incr(ipKey);
      await redisService.getClient().expire(ipKey, 300); // 5 minutes

      // Track teacher-based attempts
      const teacherAttempts = await redisService.getClient().incr(teacherKey);
      await redisService.getClient().expire(teacherKey, 300); // 5 minutes

      // Store detailed metrics
      const metricsKey = `login_metrics:${teacherId}`;
      const existingMetrics = await redisService.get(metricsKey);

      let metrics: LoginAttemptMetrics;
      if (existingMetrics) {
        metrics = JSON.parse(existingMetrics);
        metrics.attempts++;
        metrics.lastAttempt = new Date();
        if (!metrics.suspiciousIPs.includes(ipAddress) && ipAttempts > this.SUSPICIOUS_LOGIN_THRESHOLD) {
          metrics.suspiciousIPs.push(ipAddress);
        }
      } else {
        metrics = {
          attempts: 1,
          firstAttempt: new Date(),
          lastAttempt: new Date(),
          suspiciousIPs: ipAttempts > this.SUSPICIOUS_LOGIN_THRESHOLD ? [ipAddress] : []
        };
      }

      await redisService.set(metricsKey, JSON.stringify(metrics), 86400); // 24 hours

      // Return true if this appears to be the first login from this location
      return !existingMetrics;
    } catch (error) {
      console.error(`‚ùå Login metrics tracking failed:`, error);
      return false;
    }
  }

  // SECURITY 11: Detect suspicious login activity
  private static async detectSuspiciousActivity(teacherId: string, req: Request): Promise<boolean> {
    try {
      const checks = await Promise.all([
        this.checkRapidLoginAttempts(teacherId),
        this.checkUnusualLocation(teacherId, req),
        this.checkDeviceFingerprint(teacherId, req),
        this.checkTimeBasedAnomalies(teacherId)
      ]);

      const suspiciousFlags = checks.filter(Boolean).length;
      const isSuspicious = suspiciousFlags >= 2; // Require 2+ flags for suspicious classification

      if (isSuspicious) {
        console.warn(`üö® Suspicious login detected for teacher ${teacherId}: ${suspiciousFlags} flags`);
      }

      return isSuspicious;
    } catch (error) {
      console.error(`‚ùå Suspicious activity detection failed:`, error);
      return false;
    }
  }

  // Check for rapid login attempts
  private static async checkRapidLoginAttempts(teacherId: string): Promise<boolean> {
    const key = `rapid_logins:${teacherId}`;
    const attempts = await redisService.getClient().incr(key);
    await redisService.getClient().expire(key, 300); // 5 minutes

    return attempts > 3; // More than 3 logins in 5 minutes
  }

  // Check for unusual geographic location
  private static async checkUnusualLocation(teacherId: string, req: Request): Promise<boolean> {
    const currentCountry = req.headers['cf-ipcountry'] as string;
    if (!currentCountry) return false;

    const locationKey = `teacher_locations:${teacherId}`;
    const knownLocations = await redisService.get(locationKey);

    if (!knownLocations) {
      // First time login, store location
      await redisService.set(locationKey, JSON.stringify([currentCountry]), 86400 * 30); // 30 days
      return false;
    }

    const locations: string[] = JSON.parse(knownLocations);
    const isNewLocation = !locations.includes(currentCountry);

    if (isNewLocation) {
      // Add new location
      locations.push(currentCountry);
      await redisService.set(locationKey, JSON.stringify(locations), 86400 * 30);
    }

    return isNewLocation;
  }

  // Check for unusual device fingerprint
  private static async checkDeviceFingerprint(teacherId: string, req: Request): Promise<boolean> {
    const currentFingerprint = SecureJWTService.createDeviceFingerprint(req);
    const fingerprintKey = `teacher_devices:${teacherId}`;
    const knownDevices = await redisService.get(fingerprintKey);

    if (!knownDevices) {
      // First time login, store device
      await redisService.set(fingerprintKey, JSON.stringify([currentFingerprint]), 86400 * 30); // 30 days
      return false;
    }

    const devices: string[] = JSON.parse(knownDevices);
    const isNewDevice = !devices.includes(currentFingerprint);

    if (isNewDevice) {
      // Add new device (limit to 5 devices)
      devices.push(currentFingerprint);
      if (devices.length > 5) {
        devices.shift(); // Remove oldest device
      }
      await redisService.set(fingerprintKey, JSON.stringify(devices), 86400 * 30);
    }

    return isNewDevice;
  }

  // Check for time-based anomalies
  private static async checkTimeBasedAnomalies(teacherId: string): Promise<boolean> {
    const now = new Date();
    const hour = now.getHours();

    // Flag logins outside typical work hours (6 AM - 10 PM)
    if (hour < 6 || hour > 22) {
      return true;
    }

    // Flag weekend logins (basic check - in production, consider school schedules)
    const dayOfWeek = now.getDay();
    if (dayOfWeek === 0 || dayOfWeek === 6) {
      return true;
    }

    return false;
  }

  // SECURITY 12: Validate session integrity
  private static async validateSessionIntegrity(
    sessionData: SecureSessionData, 
    req: Request
  ): Promise<boolean> {
    try {
      // Check device fingerprint consistency
      const currentFingerprint = SecureJWTService.createDeviceFingerprint(req);
      if (sessionData.deviceFingerprint !== currentFingerprint) {
        console.warn(`üö® Device fingerprint mismatch for session ${sessionData.sessionId}`);
        return false;
      }

      // Check for session hijacking indicators
      if (sessionData.ipAddress !== req.ip) {
        console.warn(`üö® IP address changed for session ${sessionData.sessionId}: ${sessionData.ipAddress} -> ${req.ip}`);
        // Don't immediately fail - IP can change legitimately, but log for monitoring
      }

      // Check session age
      const sessionAge = Date.now() - sessionData.createdAt.getTime();
      const maxAge = 24 * 60 * 60 * 1000; // 24 hours
      if (sessionAge > maxAge) {
        console.warn(`üö® Session expired due to age: ${sessionData.sessionId}`);
        return false;
      }

      // Check last activity
      const inactiveTime = Date.now() - sessionData.lastActivity.getTime();
      const maxInactiveTime = 4 * 60 * 60 * 1000; // 4 hours
      if (inactiveTime > maxInactiveTime) {
        console.warn(`üö® Session expired due to inactivity: ${sessionData.sessionId}`);
        return false;
      }

      return true;
    } catch (error) {
      console.error(`‚ùå Session integrity validation failed:`, error);
      return false;
    }
  }

  // SECURITY 13: Store session metadata for monitoring
  private static async storeSessionMetadata(
    sessionId: string, 
    teacherId: string, 
    req: Request
  ): Promise<void> {
    const metadata = {
      sessionId,
      teacherId,
      ipAddress: req.ip,
      userAgent: req.headers['user-agent'],
      createdAt: new Date().toISOString(),
      country: req.headers['cf-ipcountry'],
      region: req.headers['cf-ipregion']
    };

    await redisService.set(
      `session_metadata:${sessionId}`, 
      JSON.stringify(metadata), 
      86400 // 24 hours
    );
  }

  // SECURITY 14: Alert on suspicious session creation
  private static async alertSuspiciousSession(
    teacherId: string, 
    sessionId: string, 
    req: Request
  ): Promise<void> {
    const alert = {
      type: 'suspicious_session',
      teacherId,
      sessionId,
      timestamp: new Date().toISOString(),
      ip: req.ip,
      userAgent: req.headers['user-agent'],
      country: req.headers['cf-ipcountry'],
      severity: 'medium'
    };

    console.warn(`üö® SUSPICIOUS SESSION ALERT:`, alert);

    // Store alert for security monitoring
    await redisService.set(
      `security_alert:${sessionId}`, 
      JSON.stringify(alert), 
      86400 * 7 // 7 days
    );

    // In production: send to security monitoring system
    // await securityMonitoringService.sendAlert(alert);
  }

  // SECURITY 15: Invalidate session with reason logging
  static async invalidateSession(sessionId: string, reason: string): Promise<void> {
    try {
      // Get session data before deletion for logging
      const sessionData = await this.getSecureSession(sessionId);

      // Remove encrypted session
      await redisService.getClient().del(`secure_session:${sessionId}`);

      // Remove from teacher's active sessions
      if (sessionData) {
        await redisService.getClient().srem(`teacher_sessions:${sessionData.teacherId}`, sessionId);
      }

      // Remove metadata
      await redisService.getClient().del(`session_metadata:${sessionId}`);

      // Log invalidation
      const invalidationLog = {
        sessionId,
        teacherId: sessionData?.teacherId,
        reason,
        timestamp: new Date().toISOString()
      };

      await redisService.set(
        `session_invalidation:${sessionId}`, 
        JSON.stringify(invalidationLog), 
        86400 * 7 // 7 days
      );

      console.log(`üîí Session invalidated: ${sessionId} - Reason: ${reason}`);
    } catch (error) {
      console.error(`‚ùå Session invalidation failed for ${sessionId}:`, error);
    }
  }

  // Helper method to remove corrupted sessions
  private static async removeCorruptedSession(sessionId: string): Promise<void> {
    await this.invalidateSession(sessionId, 'Corrupted session data');
  }

  // SECURITY 16: Get security metrics for monitoring
  static async getSecurityMetrics(): Promise<{
    activeSessions: number;
    suspiciousSessions: number;
    securityAlerts: number;
    sessionInvalidations: number;
  }> {
    try {
      const [sessionKeys, alertKeys, invalidationKeys] = await Promise.all([
        redisService.getClient().keys('secure_session:*'),
        redisService.getClient().keys('security_alert:*'),
        redisService.getClient().keys('session_invalidation:*')
      ]);

      // Count suspicious sessions
      let suspiciousSessions = 0;
      for (const key of sessionKeys) {
        const sessionId = key.replace('secure_session:', '');
        const sessionData = await this.getSecureSession(sessionId);
        if (sessionData?.isSuspicious) {
          suspiciousSessions++;
        }
      }

      return {
        activeSessions: sessionKeys.length,
        suspiciousSessions,
        securityAlerts: alertKeys.length,
        sessionInvalidations: invalidationKeys.length
      };
    } catch (error) {
      console.error('‚ùå Failed to get security metrics:', error);
      return { activeSessions: 0, suspiciousSessions: 0, securityAlerts: 0, sessionInvalidations: 0 };
    }
  }

  // SECURITY 17: Cleanup expired sessions and alerts (periodic maintenance)
  static async performSecurityCleanup(): Promise<void> {
    try {
      console.log('üßπ Starting security cleanup...');

      const patterns = [
        'security_alert:*',
        'session_invalidation:*', 
        'login_metrics:*',
        'teacher_locations:*',
        'teacher_devices:*'
      ];

      let cleanupCount = 0;
      for (const pattern of patterns) {
        const keys = await redisService.getClient().keys(pattern);
        for (const key of keys) {
          const ttl = await redisService.getClient().ttl(key);
          if (ttl <= 0) {
            await redisService.getClient().del(key);
            cleanupCount++;
          }
        }
      }

      if (cleanupCount > 0) {
        console.log(`üßπ Cleaned up ${cleanupCount} expired security records`);
      }
    } catch (error) {
      console.error('‚ùå Security cleanup failed:', error);
    }
  }
}

// Start periodic security cleanup
if (process.env.NODE_ENV !== 'test') {
  setInterval(() => {
    SecureSessionService.performSecurityCleanup();
  }, SecureSessionService['CLEANUP_INTERVAL']);
}

/**
 * Service Manager for ClassWaves Backend
 * Manages initialization and health of all services
 */

import { redisService } from './redis.service';
import { databricksService } from './databricks.service';
import { emailService } from './email.service';

interface ServiceStatus {
  name: string;
  status: 'healthy' | 'unhealthy' | 'initializing';
  lastCheck: Date;
  error?: string;
  connectedTime?: Date;
}

class ServiceManager {
  private services: Map<string, ServiceStatus> = new Map();

  /**
   * Initialize all services
   */
  async initializeServices(): Promise<boolean> {
    console.log('üöÄ Initializing ClassWaves services...');

    let allHealthy = true;

    // Initialize Redis
    try {
      this.updateServiceStatus('redis', 'initializing');
      if (!redisService.isConnected()) {
        // Redis connection handled automatically by ioredis
      }
      this.updateServiceStatus('redis', 'healthy', undefined, new Date());
      console.log('‚úÖ Redis service initialized');
    } catch (error) {
      console.error('‚ùå Redis service initialization failed:', error);
      this.updateServiceStatus('redis', 'unhealthy', error instanceof Error ? error.message : String(error));
      allHealthy = false;
    }

    // Initialize Databricks (skip in test environment unless explicitly enabled)
    if ((process.env.NODE_ENV !== 'test' || process.env.DATABRICKS_ENABLED === 'true') && process.env.DATABRICKS_ENABLED !== 'false') {
      try {
        this.updateServiceStatus('databricks', 'initializing');
        await databricksService.connect();
        this.updateServiceStatus('databricks', 'healthy', undefined, new Date());
        console.log('‚úÖ Databricks service initialized');
      } catch (error) {
        console.error('‚ùå Databricks service initialization failed:', error);
        this.updateServiceStatus('databricks', 'unhealthy', error instanceof Error ? error.message : String(error));
        allHealthy = false;
      }
    } else {
      if (process.env.NODE_ENV === 'test' && process.env.DATABRICKS_ENABLED === 'true') {
        this.updateServiceStatus('databricks', 'unhealthy', 'Failed to initialize in test mode');
        console.log('‚ùå Databricks service failed to initialize in test mode');
      } else {
        this.updateServiceStatus('databricks', 'healthy', 'Skipped in test environment');
        console.log('‚ö†Ô∏è Databricks service skipped (test environment)');
      }
    }

    // Initialize Email Service
    try {
      this.updateServiceStatus('email', 'initializing');
      await emailService.initialize();
      this.updateServiceStatus('email', 'healthy', undefined, new Date());
      console.log('‚úÖ Email service initialized');
    } catch (error) {
      console.error('‚ùå Email service initialization failed:', error);
      this.updateServiceStatus('email', 'unhealthy', error instanceof Error ? error.message : String(error));

      // Allow degraded mode for development
      if (process.env.NODE_ENV !== 'production') {
        console.warn('‚ö†Ô∏è Running without email service in development mode');
      } else {
        allHealthy = false;
      }
    }

    if (allHealthy) {
      console.log('üéâ All services initialized successfully');
    } else {
      console.warn('‚ö†Ô∏è Some services failed to initialize - check logs above');
    }

    return allHealthy;
  }

  /**
   * Get current status of all services
   */
  getServiceStatus(): ServiceStatus[] {
    return Array.from(this.services.values());
  }

  /**
   * Get specific service status
   */
  getStatus(serviceName: string): ServiceStatus | undefined {
    return this.services.get(serviceName);
  }

  /**
   * Update service status
   */
  private updateServiceStatus(
    name: string, 
    status: 'healthy' | 'unhealthy' | 'initializing',
    error?: string,
    connectedTime?: Date
  ): void {
    this.services.set(name, {
      name,
      status,
      lastCheck: new Date(),
      error,
      connectedTime
    });
  }

  /**
   * Health check for all services
   */
  async performHealthCheck(): Promise<{ healthy: boolean; details: ServiceStatus[] }> {
    console.log('üîç Performing service health check...');

    // Check Redis
    try {
      const isRedisHealthy = redisService.isConnected();
      this.updateServiceStatus('redis', isRedisHealthy ? 'healthy' : 'unhealthy');
    } catch (error) {
      this.updateServiceStatus('redis', 'unhealthy', error instanceof Error ? error.message : String(error));
    }

    // Check Databricks (simple query)
    try {
      await databricksService.query('SELECT 1 as health_check');
      this.updateServiceStatus('databricks', 'healthy');
    } catch (error) {
      this.updateServiceStatus('databricks', 'unhealthy', error instanceof Error ? error.message : String(error));
    }

    // Check Email service
    try {
      const emailHealth = await emailService.getHealthStatus();
      this.updateServiceStatus('email', emailHealth.status === 'degraded' ? 'unhealthy' : emailHealth.status, JSON.stringify(emailHealth.details));
    } catch (error) {
      this.updateServiceStatus('email', 'unhealthy', error instanceof Error ? error.message : String(error));
    }

    const statuses = this.getServiceStatus();
    const healthy = statuses.every(s => s.status === 'healthy');

    console.log(`üè• Health check complete. Overall status: ${healthy ? 'HEALTHY' : 'UNHEALTHY'}`);

    return { healthy, details: statuses };
  }

  /**
   * Graceful shutdown of all services
   */
  async shutdown(): Promise<void> {
    console.log('üõë Shutting down services...');

    try {
      await redisService.disconnect();
      console.log('‚úÖ Redis disconnected');
    } catch (error) {
      console.error('‚ùå Redis disconnect failed:', error);
    }

    try {
      await databricksService.disconnect();
      console.log('‚úÖ Databricks disconnected');
    } catch (error) {
      console.error('‚ùå Databricks disconnect failed:', error);
    }

    console.log('üèÅ Service shutdown complete');
  }

  /**
   * Get email service instance
   */
  getEmailService() {
    return emailService;
  }

  /**
   * Get Redis service instance
   */
  getRedisService(): any {
    return redisService;
  }

  /**
   * Get Databricks service instance
   */
  getDatabricksService(): any {
    return databricksService;
  }

  /**
   * Check if all critical services are healthy
   */
  isHealthy(): boolean {
    const statuses = this.getServiceStatus();
    return statuses.every(s => s.status === 'healthy');
  }
}

// Export singleton instance
export const serviceManager = new ServiceManager();

/**
 * Teacher Prompt Service
 * 
 * Generates contextual teaching prompts from AI analysis insights with:
 * - COPPA compliance (group-level analysis only, no individual student identification)
 * - Subject-specific and phase-aware prompt generation
 * - Effectiveness scoring and rate limiting
 * - Comprehensive audit logging
 */

import { z } from 'zod';
import { databricksService } from './databricks.service';
import type { Tier1Insights, Tier2Insights } from '../types/ai-analysis.types';
import type { TeacherPrompt, PromptCategory, PromptPriority, PromptTiming, SessionPhase, SubjectArea } from '../types/teacher-guidance.types';

// ============================================================================
// Input Validation Schemas
// ============================================================================

const promptContextSchema = z.object({
  sessionPhase: z.enum(['opening', 'development', 'synthesis', 'closure']),
  subject: z.enum(['math', 'science', 'literature', 'history', 'general']),
  learningObjectives: z.array(z.string().min(1).max(200)).max(5),
  groupSize: z.number().min(1).max(8),
  sessionDuration: z.number().min(1).max(480), // minutes
  sessionId: z.string().uuid(),
  groupId: z.string().uuid(),
  teacherId: z.string().uuid()
});

const promptGenerationOptionsSchema = z.object({
  maxPrompts: z.number().min(1).max(15).default(5),
  priorityFilter: z.enum(['all', 'high', 'medium', 'low']).default('all'),
  categoryFilter: z.array(z.enum(['facilitation', 'deepening', 'redirection', 'collaboration', 'assessment', 'energy', 'clarity'])).optional(),
  includeEffectivenessScore: z.boolean().default(true)
}).optional();

// ============================================================================
// Teacher Prompt Service Types
// ============================================================================

interface PromptGenerationContext {
  sessionId: string;
  groupId: string;
  teacherId: string;
  sessionPhase: SessionPhase;
  subject: SubjectArea;
  learningObjectives: string[];
  groupSize: number;
  sessionDuration: number;
}

interface PromptMetrics {
  totalGenerated: number;
  byCategory: Record<string, number>;
  byPriority: Record<string, number>;
  effectivenessAverage: number;
}

// ============================================================================
// Teacher Prompt Service
// ============================================================================

export class TeacherPromptService {
  private readonly config = {
    maxPromptsPerSession: parseInt(process.env.TEACHER_PROMPT_MAX_PER_SESSION || '15'),
    promptExpirationMinutes: parseInt(process.env.TEACHER_PROMPT_EXPIRATION_MINUTES || '30'),
    effectivenessScoreWeight: parseFloat(process.env.TEACHER_PROMPT_EFFECTIVENESS_WEIGHT || '0.3'),
    subjectAware: process.env.TEACHER_PROMPT_SUBJECT_AWARE === 'true',
    enableHighPrioritySound: process.env.TEACHER_ALERT_HIGH_PRIORITY_SOUND === 'true'
  };

  private promptCache = new Map<string, TeacherPrompt[]>();
  private sessionMetrics = new Map<string, PromptMetrics>();

  constructor() {
    console.log('üß† Teacher Prompt Service initialized', {
      maxPromptsPerSession: this.config.maxPromptsPerSession,
      subjectAware: this.config.subjectAware,
      effectivenessScoreWeight: this.config.effectivenessScoreWeight
    });
  }

  // ============================================================================
  // Public Methods
  // ============================================================================

  /**
   * Generate contextual teaching prompts from AI insights
   * 
   * ‚úÖ COMPLIANCE: Group-level analysis only (no individual student identification)
   * ‚úÖ SECURITY: Input validation with Zod schemas
   * ‚úÖ AUDIT: Comprehensive logging for AI-generated teacher guidance
   * ‚úÖ RATE LIMITING: Maximum 15 prompts per session
   */
  async generatePrompts(
    insights: Tier1Insights | Tier2Insights,
    context: PromptGenerationContext,
    options?: z.infer<typeof promptGenerationOptionsSchema>
  ): Promise<TeacherPrompt[]> {
    const startTime = Date.now();

    try {
      // ‚úÖ SECURITY: Input validation
      const validatedContext = promptContextSchema.parse(context);
      const validatedOptions = promptGenerationOptionsSchema.parse(options || {});

      // ‚úÖ RATE LIMITING: Check session prompt limit
      await this.checkRateLimit(validatedContext.sessionId);

      // ‚úÖ COMPLIANCE: Audit logging for AI-generated teacher guidance
      await this.auditLog({
        eventType: 'teacher_prompt_generation',
        actorId: 'system',
        targetType: 'teacher_guidance',
        targetId: validatedContext.sessionId,
        educationalPurpose: 'Generate contextual teaching prompts to improve group discussion quality',
        complianceBasis: 'legitimate_educational_interest',
        sessionId: validatedContext.sessionId,
        groupId: validatedContext.groupId,
        teacherId: validatedContext.teacherId
      });

      // Generate prompts based on insights type
      let prompts: TeacherPrompt[];
      if (this.isTier1Insights(insights)) {
        prompts = await this.generateFromTier1Insights(insights, validatedContext, validatedOptions);
      } else {
        prompts = await this.generateFromTier2Insights(insights, validatedContext, validatedOptions);
      }

      // Apply filters and limits
      prompts = this.applyFilters(prompts, validatedOptions || {});
      prompts = prompts.slice(0, (validatedOptions && validatedOptions.maxPrompts) || 5);

      // ‚úÖ DATABASE: Store generated prompts in database
      await this.storePromptsInDatabase(prompts, validatedContext);

      // Cache prompts and update metrics
      this.cachePrompts(validatedContext.sessionId, prompts);
      await this.updateSessionMetrics(validatedContext.sessionId, prompts);

      const processingTime = Date.now() - startTime;
      console.log(`‚úÖ Generated ${prompts.length} prompts for session ${validatedContext.sessionId} in ${processingTime}ms`);

      return prompts;

    } catch (error) {
      const processingTime = Date.now() - startTime;
      console.error(`‚ùå Failed to generate prompts for session ${context.sessionId}:`, error);

      // ‚úÖ COMPLIANCE: Audit log for errors
      await this.auditLog({
        eventType: 'teacher_prompt_generation_error',
        actorId: 'system',
        targetType: 'teacher_guidance',
        targetId: context.sessionId,
        educationalPurpose: 'Log prompt generation error for system monitoring',
        complianceBasis: 'system_administration',
        sessionId: context.sessionId,
        error: (error as Error).message
      });

      throw error;
    }
  }

  /**
   * Get cached prompts for a session
   */
  getSessionPrompts(sessionId: string): TeacherPrompt[] {
    return this.promptCache.get(sessionId) || [];
  }

  /**
   * Mark prompt as acknowledged/used/dismissed for effectiveness tracking
   */
  async recordPromptInteraction(
    promptId: string,
    sessionId: string,
    teacherId: string,
    interactionType: 'acknowledged' | 'used' | 'dismissed',
    feedback?: { rating: number; text: string }
  ): Promise<void> {
    try {
      // ‚úÖ COMPLIANCE: Audit logging for teacher interaction data
      await this.auditLog({
        eventType: 'prompt_interaction',
        actorId: teacherId,
        targetType: 'teacher_prompt',
        targetId: promptId,
        educationalPurpose: 'Track teacher engagement with AI-generated guidance for system improvement',
        complianceBasis: 'legitimate_educational_interest',
        sessionId,
        interactionType,
        feedbackRating: feedback?.rating
      });

      // Store interaction in database for analytics
      await this.recordPromptInteractionToDB(promptId, sessionId, teacherId, interactionType, feedback);

    } catch (error) {
      console.error(`‚ùå Failed to record prompt interaction:`, error);
      throw error;
    }
  }

  /**
   * Get session prompt metrics
   */
  getSessionMetrics(sessionId: string): PromptMetrics | null {
    return this.sessionMetrics.get(sessionId) || null;
  }

  /**
   * Get active prompts for a specific session
   * 
   * ‚úÖ COMPLIANCE: Audit logging for prompt access
   * ‚úÖ PERFORMANCE: Combines cache and database with deduplication
   * ‚úÖ SECURITY: Input validation and error handling
   * ‚úÖ REAL-TIME: Fresh data with cache optimization
   */
  async getActivePrompts(sessionId: string, options?: {
    includeExpired?: boolean;
    maxAge?: number; // minutes
    priorityFilter?: PromptPriority[];
  }): Promise<TeacherPrompt[]> {
    const startTime = Date.now();

    try {
      // ‚úÖ SECURITY: Input validation
      if (!sessionId || typeof sessionId !== 'string') {
        throw new Error('Invalid sessionId provided');
      }

      // ‚úÖ COMPLIANCE: Audit logging for prompt access
      await this.auditLog({
        eventType: 'teacher_prompt_access',
        actorId: 'system',
        targetType: 'teacher_guidance',
        targetId: sessionId,
        educationalPurpose: 'Retrieve active teacher prompts for real-time guidance',
        complianceBasis: 'legitimate_educational_interest',
        sessionId
      });

      const now = new Date();
      const maxAge = options?.maxAge || this.config.promptExpirationMinutes;
      const cutoffTime = new Date(now.getTime() - maxAge * 60000);

      // Step 1: Get prompts from in-memory cache
      const cachedPrompts = this.promptCache.get(sessionId) || [];

      // Step 2: Get prompts from database for persistence/recovery
      let dbPrompts: TeacherPrompt[] = [];
      try {
        const dbResults = await databricksService.query(
          `SELECT 
             id,
             session_id,
             teacher_id,
             prompt_id,
             group_id,
             prompt_category,
             priority_level,
             prompt_message,
             prompt_context,
             suggested_timing,
             session_phase,
             subject_area,
             target_metric,
             learning_objectives,
             generated_at,
             acknowledged_at,
             used_at,
             dismissed_at,
             expires_at,
             effectiveness_score,
             feedback_rating,
             feedback_text,
             created_at,
             updated_at
           FROM classwaves.ai_insights.teacher_guidance_metrics 
           WHERE session_id = ? 
           AND generated_at >= ?
           ${!options?.includeExpired ? 'AND expires_at > ?' : ''}
           AND dismissed_at IS NULL
           ORDER BY priority_level DESC, generated_at DESC`,
          options?.includeExpired 
            ? [sessionId, cutoffTime.toISOString()]
            : [sessionId, cutoffTime.toISOString(), now.toISOString()]
        );

        // Transform database results to TeacherPrompt objects
        dbPrompts = dbResults.map(row => this.transformDbToPrompt(row));
      } catch (dbError) {
        console.warn('‚ö†Ô∏è Database query failed for active prompts, using cache only:', dbError);
        // Continue with cache-only results for graceful degradation
      }

      // Step 3: Merge and deduplicate prompts (cache takes precedence)
      const allPrompts = new Map<string, TeacherPrompt>();

      // Add database prompts first
      dbPrompts.forEach(prompt => allPrompts.set(prompt.id, prompt));

      // Add cached prompts (overwrites DB with fresher data)
      cachedPrompts.forEach(prompt => allPrompts.set(prompt.id, prompt));

      // Step 4: Apply filters
      let filteredPrompts = Array.from(allPrompts.values());

      // Filter by expiration (unless includeExpired is true)
      if (!options?.includeExpired) {
        filteredPrompts = filteredPrompts.filter(p => p.expiresAt > now);
      }

      // Filter by priority if specified
      if (options?.priorityFilter && options.priorityFilter.length > 0) {
        filteredPrompts = filteredPrompts.filter(p => 
          options.priorityFilter!.includes(p.priority)
        );
      }

      // Filter by age
      filteredPrompts = filteredPrompts.filter(p => 
        p.generatedAt >= cutoffTime
      );

      // Step 5: Sort by priority and creation time
      filteredPrompts.sort((a, b) => {
        const priorityOrder = { 'high': 3, 'medium': 2, 'low': 1 };
        const aPriority = priorityOrder[a.priority] || 0;
        const bPriority = priorityOrder[b.priority] || 0;

        if (aPriority !== bPriority) {
          return bPriority - aPriority; // High priority first
        }

        return b.generatedAt.getTime() - a.generatedAt.getTime(); // Newest first
      });

      const processingTime = Date.now() - startTime;
      console.log(`‚úÖ Retrieved ${filteredPrompts.length} active prompts for session ${sessionId} in ${processingTime}ms`);

      return filteredPrompts;

    } catch (error) {
      const processingTime = Date.now() - startTime;
      console.error(`‚ùå Failed to get active prompts for session ${sessionId}:`, error);

      // ‚úÖ COMPLIANCE: Audit log for errors
      await this.auditLog({
        eventType: 'teacher_prompt_access_error',
        actorId: 'system',
        targetType: 'teacher_guidance',
        targetId: sessionId,
        educationalPurpose: 'Log prompt access error for system monitoring',
        complianceBasis: 'system_administration',
        sessionId,
        error: (error as Error).message
      });

      throw error;
    }
  }

  /**
   * Transform database row to TeacherPrompt object
   */
  private transformDbToPrompt(row: any): TeacherPrompt {
    return {
      id: row.prompt_id || row.id,
      sessionId: row.session_id,
      teacherId: row.teacher_id,
      groupId: row.group_id,
      category: row.prompt_category,
      priority: row.priority_level,
      message: row.prompt_message,
      context: row.prompt_context,
      suggestedTiming: row.suggested_timing,
      generatedAt: new Date(row.generated_at),
      expiresAt: new Date(row.expires_at),
      acknowledgedAt: row.acknowledged_at ? new Date(row.acknowledged_at) : undefined,
      usedAt: row.used_at ? new Date(row.used_at) : undefined,
      dismissedAt: row.dismissed_at ? new Date(row.dismissed_at) : undefined,
      sessionPhase: row.session_phase,
      subject: row.subject_area,
      targetMetric: row.target_metric,
      learningObjectives: row.learning_objectives ? JSON.parse(row.learning_objectives) : undefined,
      effectivenessScore: row.effectiveness_score,
      feedbackRating: row.feedback_rating,
      feedbackText: row.feedback_text,
      createdAt: new Date(row.created_at),
      updatedAt: new Date(row.updated_at)
    };
  }

  /**
   * Clear expired prompts and cleanup
   */
  async cleanup(): Promise<void> {
    const now = new Date();
    let cleanedCount = 0;

    for (const [sessionId, prompts] of Array.from(this.promptCache.entries())) {
      const activePrompts = prompts.filter(p => p.expiresAt > now);

      if (activePrompts.length !== prompts.length) {
        if (activePrompts.length === 0) {
          this.promptCache.delete(sessionId);
        } else {
          this.promptCache.set(sessionId, activePrompts);
        }
        cleanedCount += prompts.length - activePrompts.length;
      }
    }

    if (cleanedCount > 0) {
      console.log(`üßπ Cleaned up ${cleanedCount} expired prompts`);
    }
  }

  // ============================================================================
  // Private Methods - Tier 1 Insights Processing
  // ============================================================================

  private async generateFromTier1Insights(
    insights: Tier1Insights,
    context: PromptGenerationContext,
    options: z.infer<typeof promptGenerationOptionsSchema>
  ): Promise<TeacherPrompt[]> {
    const prompts: TeacherPrompt[] = [];

    // Analyze topical cohesion
    if (insights.topicalCohesion < 0.6) {
      prompts.push(this.createPrompt({
        category: 'redirection',
        priority: insights.topicalCohesion < 0.4 ? 'high' : 'medium',
        message: this.getTopicalCohesionPrompt(insights.topicalCohesion, context),
        context: `Group showing topic drift (score: ${(insights.topicalCohesion * 100).toFixed(0)}%)`,
        suggestedTiming: insights.topicalCohesion < 0.4 ? 'immediate' : 'next_break',
        targetMetric: 'topicalCohesion',
        sessionPhase: context.sessionPhase,
        subject: context.subject,
        sessionId: context.sessionId,
        teacherId: context.teacherId,
        groupId: context.groupId
      }));
    }

    // Analyze conceptual density
    if (insights.conceptualDensity < 0.5) {
      prompts.push(this.createPrompt({
        category: 'deepening',
        priority: 'medium',
        message: this.getConceptualDensityPrompt(insights.conceptualDensity, context),
        context: `Discussion needs more depth (score: ${(insights.conceptualDensity * 100).toFixed(0)}%)`,
        suggestedTiming: 'next_break',
        targetMetric: 'conceptualDensity',
        sessionPhase: context.sessionPhase,
        subject: context.subject,
        sessionId: context.sessionId,
        teacherId: context.teacherId,
        groupId: context.groupId
      }));
    }

    // Process any additional insights
    for (const insight of insights.insights) {
      if (insight.severity === 'warning') {
        prompts.push(this.createPromptFromInsight(insight, context));
      }
    }

    return prompts;
  }

  // ============================================================================
  // Private Methods - Tier 2 Insights Processing
  // ============================================================================

  private async generateFromTier2Insights(
    insights: Tier2Insights,
    context: PromptGenerationContext,
    options: z.infer<typeof promptGenerationOptionsSchema>
  ): Promise<TeacherPrompt[]> {
    const prompts: TeacherPrompt[] = [];

    // Analyze argumentation quality
    if (insights.argumentationQuality.score < 0.6) {
      prompts.push(this.createPrompt({
        category: 'deepening',
        priority: 'high',
        message: this.getArgumentationPrompt(insights.argumentationQuality, context),
        context: `Low argumentation quality (score: ${(insights.argumentationQuality.score * 100).toFixed(0)}%)`,
        suggestedTiming: 'immediate',
        targetMetric: 'argumentationQuality',
        sessionPhase: context.sessionPhase,
        subject: context.subject,
        sessionId: context.sessionId,
        teacherId: context.teacherId,
        groupId: context.groupId
      }));
    }

    // Analyze collaboration patterns
    if (insights.collaborationPatterns.inclusivity < 0.5) {
      prompts.push(this.createPrompt({
        category: 'collaboration',
        priority: 'high',
        message: this.getInclusivityPrompt(insights.collaborationPatterns, context),
        context: `Some voices may not be heard (inclusivity: ${(insights.collaborationPatterns.inclusivity * 100).toFixed(0)}%)`,
        suggestedTiming: 'immediate',
        targetMetric: 'collaborationPatterns.inclusivity',
        sessionPhase: context.sessionPhase,
        subject: context.subject,
        sessionId: context.sessionId,
        teacherId: context.teacherId,
        groupId: context.groupId
      }));
    }

    // Analyze emotional arc
    if (insights.collectiveEmotionalArc.trajectory === 'descending') {
      prompts.push(this.createPrompt({
        category: 'energy',
        priority: 'medium',
        message: this.getEnergyPrompt(insights.collectiveEmotionalArc, context),
        context: `Group energy is declining`,
        suggestedTiming: 'immediate',
        targetMetric: 'collectiveEmotionalArc.trajectory',
        sessionPhase: context.sessionPhase,
        subject: context.subject,
        sessionId: context.sessionId,
        teacherId: context.teacherId,
        groupId: context.groupId
      }));
    }

    // Process recommendations
    for (const recommendation of insights.recommendations) {
      if (recommendation.priority === 'high' || recommendation.priority === 'medium') {
        prompts.push(this.createPromptFromRecommendation(recommendation, context));
      }
    }

    return prompts;
  }

  // ============================================================================
  // Private Methods - Prompt Creation
  // ============================================================================

  private createPrompt(data: {
    category: PromptCategory;
    priority: PromptPriority;
    message: string;
    context: string;
    suggestedTiming: PromptTiming;
    targetMetric?: string;
    sessionPhase: SessionPhase;
    subject: SubjectArea;
    sessionId: string;
    teacherId: string;
    groupId?: string;
  }): TeacherPrompt {
    const now = new Date();
    return {
      id: this.generatePromptId(),
      sessionId: data.sessionId,
      teacherId: data.teacherId,
      groupId: data.groupId,
      category: data.category,
      priority: data.priority,
      message: data.message,
      context: data.context,
      suggestedTiming: data.suggestedTiming,
      generatedAt: now,
      expiresAt: new Date(now.getTime() + this.config.promptExpirationMinutes * 60000),
      sessionPhase: data.sessionPhase,
      subject: data.subject,
      targetMetric: data.targetMetric,
      effectivenessScore: this.calculateEffectivenessScore(data.category, data.priority),
      createdAt: now,
      updatedAt: now
    };
  }

  private createPromptFromInsight(insight: any, context: PromptGenerationContext): TeacherPrompt {
    return this.createPrompt({
      category: insight.type === 'topical_cohesion' ? 'redirection' : 'deepening',
      priority: insight.severity === 'warning' ? 'high' : 'medium',
      message: insight.actionable || insight.message,
      context: insight.message,
      suggestedTiming: 'next_break',
      sessionPhase: context.sessionPhase,
      subject: context.subject,
      sessionId: context.sessionId,
      teacherId: context.teacherId,
      groupId: context.groupId
    });
  }

  private createPromptFromRecommendation(recommendation: any, context: PromptGenerationContext): TeacherPrompt {
    const categoryMap: Record<string, PromptCategory> = {
      'intervention': 'redirection',
      'praise': 'energy',
      'redirect': 'redirection',
      'deepen': 'deepening'
    };

    return this.createPrompt({
      category: categoryMap[recommendation.type] || 'facilitation',
      priority: recommendation.priority,
      message: recommendation.suggestedAction || recommendation.message,
      context: recommendation.message,
      suggestedTiming: recommendation.priority === 'high' ? 'immediate' : 'next_break',
      sessionPhase: context.sessionPhase,
      subject: context.subject,
      sessionId: context.sessionId,
      teacherId: context.teacherId,
      groupId: context.groupId
    });
  }

