/**
 * Parallel authentication operations for maximum performance
 */
export async function executeParallelAuthOperations(
  googleUser: GoogleUser,
  domain: string,
  sessionId: string,
  req: Request
): Promise<{
  school: School;
  teacher: Teacher;
  sessionStored: boolean;
  refreshTokenStored: boolean;
}> {
  console.log('üöÄ PARALLEL AUTH OPERATIONS START');
  const parallelStart = performance.now();

  try {
    // Execute database operations and token generation in parallel
    const [
      // Database operations
      { school, teacher },
      // Session storage preparation (we'll store after we have teacher data)
    ] = await Promise.all([
      // 1. Get school and teacher data using the existing optimized batch operation
      import('../services/databricks.service').then(({ databricksService }) => 
        databricksService.batchAuthOperations(googleUser, domain)
      ),
    ]);

    // Now execute session and refresh token storage in parallel
    const [sessionResult, refreshTokenResult] = await Promise.allSettled([
      // Store session with optimized Redis service
      storeSessionOptimized(sessionId, teacher, school, req),

      // Store refresh token
      redisService.storeRefreshToken(
        sessionId,
        teacher.id,
        30 * 24 * 60 * 60 // 30 days
      ),
    ]);

    const sessionStored = sessionResult.status === 'fulfilled';
    const refreshTokenStored = refreshTokenResult.status === 'fulfilled';

    // Log any storage failures but don't fail the auth if one storage operation fails
    if (sessionResult.status === 'rejected') {
      console.error('‚ùå Session storage failed:', sessionResult.reason);
    }
    if (refreshTokenResult.status === 'rejected') {
      console.error('‚ùå Refresh token storage failed:', refreshTokenResult.reason);
    }

    console.log(`üéâ PARALLEL AUTH OPERATIONS COMPLETE (${(performance.now() - parallelStart).toFixed(2)}ms)`);

    return {
      school,
      teacher,
      sessionStored,
      refreshTokenStored,
    };
  } catch (error) {
    console.error('‚ùå Parallel auth operations failed:', error);
    throw error;
  }
}

